    await page?.setBypassCSP(true);
    return page;
}

racePromises.rawText:
 async (
  promises: Promise<any>[]
): Promise<number> => {
  const indexedPromises: Array<Promise<number>> = promises.map(
    (promise, index) =>
      new Promise<number>((resolve) =>
        promise
          .then(() => resolve(index))
          .catch((err) => {
            console.log("RACE PROMISE INDEX " + index + " TIMED OUT !!!");
          })
      )
  );
  return Promise.race(indexedPromises);
}

retryClickAndWaitSelector.rawText:
 async (props: {
  /** the page element from puppeteer got with browser.pages */
  page: any;
  /** the css selector to search for */
  selector: string | string[];
  /** the options */
  selectorOptions?: any;
  /** the css selector to click after */
  selectorToClick: string;
  /**  maxium time to retry. defaults to 30 seconds. */
  maxTime: number;
}): Promise<{ success: boolean; found?: any }> => {
  const {
    page,
    selector,
    selectorOptions,
    selectorToClick,
    maxTime = 30000,
  } = props;
  return new Promise(async (resolve, reject) => {
    let totalTime = 0;
    while (totalTime < maxTime) {
      try {
        const found = await page.waitForSelector(selector, {
          ...selectorOptions,
          timeout: 1000,
        });
        resolve({ success: true, found });
        return;
      } catch (err) {
        try {
          await page.click(selectorToClick);
        } catch (insideErr) {
          //console.log('inside error ! continuing...');
        }

        totalTime += 1000;
        console.log("retrying...");
      }
    }
    resolve({ success: false });
  });
}

