}): Promise<{ success: boolean; found?: any }> => {
  const { page, selector, maxTime = 30000 } = props;
  return new Promise(async (resolve, reject) => {
    let totalTime = 0;
    while (totalTime < maxTime) {
      try {
        const found = await page.waitForSelector(selector, { timeout: 1000 });
        resolve({ success: true, found });
      } catch (err) {
        totalTime += 1000;
        console.log("retrying... " + selector + " / totalTime " + totalTime);
      }
    }
    resolve({ success: false });
  });
}

