readMarkdownFileToModel.rawText:
 async (
  absoluteFilePath: string,
  webOperationName: string,
  /**
   * Only the ones that are not from this domain without path (filter based on hostname/path)
   */
  markdownCallToActions: MarkdownCallToAction[]
): Promise<null | WebMarkdownFile> => {
  const filename = path.parse(absoluteFilePath).name;

  const projectRoot = getProjectRoot(absoluteFilePath);

  if (!projectRoot) {
    console.log("Projectroot not found");
    return null;
  }
  const mdParse = await readMarkdownFile(absoluteFilePath);
  if (!mdParse) {
    console.log("mdParse not found");
    return null;
  }

  const projectRelativeWebOperationPath =
    operations[webOperationName as keyof typeof operations];

  if (!projectRelativeWebOperationPath) return null;

  const absoluteHeadersFolderPath = path.join(
    projectRoot,
    projectRelativeWebOperationPath,
    "public/headers"
  );

  const publicHeaderFilenames = fs.existsSync(absoluteHeadersFolderPath)
    ? await fs.readdir(absoluteHeadersFolderPath, "utf8")
    : [];

  const parsedParameters = frontmatterToObject(
    mdParse.parameters,
    webMarkdownFileTsInterface.type.simplifiedSchema as SimplifiedSchema
  ) as Partial<
    ExtendedMarkdownProperties & WebMarkdownProperties & PostableProperties
  >;

  if (!parsedParameters.headerImage && publicHeaderFilenames.length) {
    /**
     *  Should be a number that depends on the absolute file path. Should be quite random because every charCode is a different number.
     */
    const uniqueDeterministicFilepathNumber = sum(
      absoluteFilePath.split("").map((letter) => letter.charCodeAt(0))
    );
    /**
     * Makes the `uniqueDeterministicFilePathNumber into a number between 0 and length - 1,
     */
    const deterministicHeaderImageNumber =
      uniqueDeterministicFilepathNumber % (publicHeaderFilenames.length - 1);

    /**
     * Gets the actual header filename
     */
    const deterministicHeaderImage =
      publicHeaderFilenames[deterministicHeaderImageNumber];

    /**
     * Puts it as backendAsset
     */
    parsedParameters.headerImage = {
      // NB: this ensures the URL isn't later altered to a backend-url (in dev mode)
      absoluteUrl: `/headers/${deterministicHeaderImage}`,
    };
  }

  if (!parsedParameters.markdownCallToActionSlugs) {
    parsedParameters.markdownCallToActionSlugs = markdownCallToActions.map(
      (x) => x.slug
    );
  }

  // attach the actual call to actions

  parsedParameters.markdownCallToActions =
    parsedParameters.markdownCallToActionSlugs
      ?.map((slug) => markdownCallToActions.find((x) => x.slug === slug))
      .filter(notEmpty) || [];

  parsedParameters.header_markdownCallToActions =
    parsedParameters.header_markdownCallToActionSlugs
      ?.map((slug) => markdownCallToActions.find((x) => x.slug === slug))
      .filter(notEmpty) || [];

  // NB: this markdown file is not always located in an operation. Yet we are trying to find it.
  const operationBasePath = findOperationBasePath(absoluteFilePath);
  const modelLocation: ModelLocation = {
    operationName: operationBasePath ? getLastFolder(operationBasePath) : null,
    projectRelativePath: makeRelative(absoluteFilePath, projectRoot),
    operationRelativePath: operationBasePath
      ? makeRelative(absoluteFilePath, operationBasePath)
      : undefined,
  };

  const name = mdParse.fileName || "untitled";
  const markdownFile: WebMarkdownFile = {
    ...modelLocation,
    ...parsedParameters,
    markdown: mdParse.raw,
    categoryStack: [],
    ...parsedParameters,
    id: name,
    createdFirstAt: Date.now(),
    deletedAt: 0,
    createdAt: Date.now(),
    language: parsedParameters.language || "english",
    updatedAt: parsedParameters.updatedAt || Date.now(),
    name,
    slug: name,
  };

  return omitUndefinedValues(markdownFile);
}

