getFolderTypescriptIndex.rawText:
 async ({
  basePath,
  filter,
  sort,
  type,
  filePath,
}: {
  /**
   * if given, basePath is ignored
   *
   */
  filePath?: string | string[];
  /**
   * is ignored if filePath is provided
   *
   * if this path is a single path anywhere in an operaiton, it will only take that operation
   *
   * otherwise, it will find all operation folders in that path
   */
  basePath?: string | string[];

  /**
   * if specified, only returns one specific type
   *
   * returns all types by default
   */
  type?: keyof IndexModels | null;

  filter?: IndexFilter;

  /**
   * TODO: make functions be sortable:
   * - alphabetic
   * - input type
   * - output type
   * - usage amount
   */
  sort?: string;
}): Promise<TypescriptIndex | null> => {
  const operationBasePath =
    basePath && !Array.isArray(basePath)
      ? findOperationBasePath(basePath)
      : undefined;

  // console.log("MMMM", { basePath, operationBasePath });

  const groupedFiles: GroupedFiles = operationBasePath
    ? { [operationBasePath]: [] }
    : filePath && filePath.length > 0
    ? makeArray(filePath).reduce((groups, filePath) => {
        const operationBasePath = findOperationBasePath(filePath);
        if (operationBasePath) {
          groups[operationBasePath] = groups[operationBasePath]
            ? groups[operationBasePath].concat(filePath)
            : [filePath];
        }
        return groups;
      }, {} as GroupedFiles)
    : (await exploreOperationFolders({ basePath })).reduce(
        (groups, operationBasePath) => {
          groups[operationBasePath] = [];
          return groups;
        },
        {} as GroupedFiles
      );

  const typescriptIndexes = await Promise.all(
    Object.keys(groupedFiles).map(async (operationBasePath) => {
      const files = groupedFiles[operationBasePath];

      const tsBuildErrors =
        type === "TsBuildError" || !type
          ? await getOperationIndexModel(
              "TsBuildError",
              operationBasePath,
              files
            )
          : [];

      const tsLintWarnings =
        type === "TsLintWarning" || !type
          ? await getOperationIndexModel(
              "TsLintWarning",
              operationBasePath,
              files
            )
          : [];

      const tsFunctions =
        type === "TsFunction" || !type
          ? await getOperationIndexModel("TsFunction", operationBasePath, files)
          : [];

      const extendedTsFuntions = await Promise.all(
        tsFunctions.map(async (tsFunction) => ({
          ...tsFunction,
          dependantFiles: tsFunction.operationName
            ? await findDependants({
                operationName: tsFunction.operationName,
                importName: tsFunction.name,
              })
            : [],
        }))
      );

      const tsVariables =
        type === "TsVariable" || !type
          ? await getOperationIndexModel("TsVariable", operationBasePath, files)
          : [];

      const tsInterfaces =
        type === "TsInterface" || !type
          ? await getOperationIndexModel(
              "TsInterface",
              operationBasePath,
              files
            )
          : [];

      const tsComments =
        type === "TsComment" || !type
          ? await getOperationIndexModel("TsComment", operationBasePath, files)
          : [];

      const tsImports =
        type === "TsImport" || !type
          ? await getOperationIndexModel("TsImport", operationBasePath, files)
          : [];

      const tsExports =
        type === "TsExport" || !type
          ? await getOperationIndexModel("TsExport", operationBasePath, files)
          : [];

      const filteredInterfaces = tsInterfaces.filter((x) =>
        filter?.interfaceIsDbModel ? x.isDbModel : true
      );

      const typescriptIndex: TypescriptIndex = {
        tsBuildErrors,
        tsLintWarnings,
        tsFunctions: extendedTsFuntions,
        tsVariables,
        tsInterfaces: filteredInterfaces,
        tsComments: tsComments.filter((comment) =>
          filter?.hasCommentTypes
            ? comment.types.find((type) =>
                filter.hasCommentTypes!.includes(type)
              )
            : true
        ),
        tsImports,
        tsExports,
      };

      return typescriptIndex;
    })
  );

  const mergedTypescriptIndex = typescriptIndexes.reduce((merged, current) => {
    if (merged) {
      const indexModels = Object.keys(merged) as (keyof TypescriptIndex)[];

      const together = mergeObjectsArray(
        indexModels.map((indexModelName) => {
          const filterOnName =
            indexModelName === "tsComments" ? () => true : uniqueNames;

          return {
            [indexModelName]: [
              ...merged[indexModelName],
              ...current[indexModelName],
            ].filter(filterOnName),
          };
        })
      ) as TypescriptIndex;

      return together;
    }

    return current;
  }, null as TypescriptIndex | null);

  return mergedTypescriptIndex;
}

getOperationIndexModel.rawText:
 async <T extends keyof IndexModels>(
  indexModelName: T,
  operationBasePath: string,
  /**
   * if provided, db result will be filtered to specific filepaths
   */
  filePaths: string[]
): Promise<IndexModels[T][]> => {
  const projectRoot = getProjectRoot(filePaths[0]);
  if (!projectRoot) return [];
  const relativeFilePaths = filePaths.map((f) =>
    f.substring(projectRoot.length)
  );
  const result = await db
    // TODO: make it more efficient by just finding the correct JSON files instead of all files in the operation.
    .get(indexModelName, { operationName: getLastFolder(operationBasePath) });

  const filterOnName =
    indexModelName === "TsComment" ? () => true : uniqueNames;

  const filtered = result
    .filter(
      (x) =>
        x.projectRelativePath &&
        (relativeFilePaths.length === 0 ||
          relativeFilePaths.includes(x.projectRelativePath))
    )
    .filter(filterOnName);

  return filtered;
}

readTypescriptFile.rawText:
 async (
  filePath: Path
): Promise<TypescriptIndex | null> => {
  const operationBasePath = findOperationBasePath(filePath);
  if (!operationBasePath) return null;
  const operationName = getLastFolder(operationBasePath);

  const operationRelativeTypescriptFilePath = getOperationRelativePath(
    filePath,
    operationBasePath
  );

  const getFileIndexes = <T extends keyof IndexModels>(
    indexModelName: T
  ): Promise<IndexModels[T][]> => {
    const result = db
      // TODO: make it more efficient by just finding the correct JSON files instead of all files in the operation.
      .get(indexModelName, { operationName })
      .then((result) =>
        result.filter((x) => {
          return (
            x.operationRelativeTypescriptFilePath ===
            operationRelativeTypescriptFilePath
          );
        })
      );

    return result;
  };

  // TODO: is this needed?
  const pathParse = path.parse(filePath);
  const isTypescriptFile = ["ts", "tsx"].includes(pathParse.ext.slice(1));
  if (!isTypescriptFile) return null;
  if (!fs.existsSync(filePath)) return null;

  const typescriptIndex: TypescriptIndex = {
    tsBuildErrors: await getFileIndexes("TsBuildError"),
    tsLintWarnings: await getFileIndexes("TsLintWarning"),
    tsFunctions: await getFileIndexes("TsFunction"),
    tsVariables: await getFileIndexes("TsVariable"),
    tsInterfaces: await getFileIndexes("TsInterface"),
    tsComments: await getFileIndexes("TsComment"),
    tsImports: await getFileIndexes("TsImport"),
    tsExports: await getFileIndexes("TsExport"),
  };

  return typescriptIndex;
}

