 (props: ReaderPageContentProps) => {
  const router = useRouter();
  const queryPath = getQueryPath(router.query);
  const [_, setIsMobileMenuEnabled] = useStore("menu.isMobileMenuEnabled");

  const {
    augmentedWordObject,
    children,
    description,
    markdownFile,
    nextQueryPath,
    previousQueryPath,
    projectRelativeMarkdownPath,
    title,
  } = props;

  const [view, setView] = useState("view" as "view" | "presentation");

  const renderBottomNavigation = () => {
    return (
      <Div className="flex flex-row w-full justify-between">
        {previousQueryPath ? (
          <ALink href={`${previousQueryPath}`}>
            <Div className="w-60 h-40 border-2 border-black rounded-3xl m-4 p-4 flex flex-col justify-center items-center">
              <P className="font-bold text-xl">
                {previousQueryPath.split("/").pop()}
              </P>
              <P className="font-bold text-6xl">‚¨Ö</P>
            </Div>
          </ALink>
        ) : (
          <Div />
        )}

        {nextQueryPath ? (
          <ALink href={`${nextQueryPath}`}>
            <Div className="w-60 h-40 border-2 border-black rounded-3xl m-4 p-4 flex flex-col justify-center items-center">
              <P className="font-bold text-xl">
                {nextQueryPath.split("/").pop()}
              </P>
              <P className="font-bold text-6xl">‚û°</P>
            </Div>
          </ALink>
        ) : (
          <Div />
        )}
      </Div>
    );
  };

  const markdownParse: MarkdownParse | null = markdownFile?.markdown
    ? mdToJsonParse(markdownFile?.markdown)
    : null;

  const renderMarkdownView = () => {
    const projectRelativeMarkdownFilePath = projectRelativeMarkdownPath;
    const projectRelativeBaseFolderPath = projectRelativeMarkdownPath
      ? getFolderJs(projectRelativeMarkdownPath)
      : undefined;

    if (
      !markdownParse ||
      projectRelativeBaseFolderPath === undefined ||
      projectRelativeMarkdownFilePath === undefined ||
      projectRelativeMarkdownFilePath === null
    ) {
      return null;
    }

    const markdownParseRenderConfig = {
      augmentedWordObject,
      projectRelativeBaseFolderPath,
      projectRelativeMarkdownFilePath,
      isDev,
      isStatic: true,
    };

    return (
      <MarkdownView
        markdownParse={markdownParse}
        markdownParseRenderConfig={markdownParseRenderConfig}
        view={view}
      />
    );
  };

  const renderPage = () => {
    const hasNoMainTitle =
      markdownParse?.content?.filter((x) => !!x.title && x.level === 1)
        .length !== 1;

    // console.log({ hasNoMainTitle, f: props.markdownFile?.name });

    return (
      <Div>
        <Div className="flex flex-row justify-between">
          {queryPath.length > 1 ? (
            <ClickableIcon
              emoji="‚¨Ö"
              onClick={() => {
                const parts = queryPath.split("/");
                parts.pop();
                const oneFolderUp = parts.join("/");
                router.push(oneFolderUp.length === 0 ? "/" : oneFolderUp);
              }}
            />
          ) : null}

          <Div className="flex flex-row">
            <Div className="lg:hidden">
              <ClickableIcon
                emoji="‚Éõ"
                onClick={() => {
                  setIsMobileMenuEnabled(true);
                }}
              />
            </Div>
            <ClickableIcon
              emoji="üëÅ"
              onClick={() => {
                setView(view === "presentation" ? "view" : "presentation");
              }}
            />
          </Div>
        </Div>

        {hasNoMainTitle && markdownFile?.name ? (
          <P className="text-3xl">{humanCase(markdownFile.name)}</P>
        ) : null}

        {renderMarkdownView()}

        {previousQueryPath || nextQueryPath ? renderBottomNavigation() : null}
      </Div>
    );
  };

  const renderNav = () => {
    return (
      <Div>
        <Div className="flex flex-row items-center">
          <ClickableIcon
            emoji="‚¨Ö"
            onClick={() => {
              const parts = queryPath.split("/");
              parts.pop();
              const oneFolderUp = parts.join("/");
              router.push(oneFolderUp.length === 0 ? "/" : oneFolderUp);
            }}
          />
          <P className="text-xl lg:text-4xl w-full">&nbsp;&nbsp;{title}</P>

          <Div className="lg:hidden">
            <ClickableIcon
              emoji="‚Éõ"
              onClick={() => {
                setIsMobileMenuEnabled(true);
              }}
            />
          </Div>
        </Div>

        {description && projectRelativeMarkdownPath ? (
          <Div>
            {renderMarkdownContent(description, {
              projectRelativeMarkdownFilePath: projectRelativeMarkdownPath,
              augmentedWordObject,
              projectRelativeBaseFolderPath: getFolderJs(
                projectRelativeMarkdownPath
              )!,
            })}
          </Div>
        ) : null}

        <Div className="flex flex-row flex-wrap">
          {children?.map((child, index) => {
            return (
              <ALink
                key={`child${child.projectRelativeMarkdownPath}-${index}`}
                href={`${queryPath}/${child.folderName}`}
              >
                <Div className="w-60 border-2 border-black rounded-3xl m-4 p-4 flex flex-col justify-center items-center">
                  <P className="font-bold text-xl">{child.title}</P>
                  {child.firstParagraph && child.projectRelativeMarkdownPath ? (
                    <Div>
                      {renderMarkdownContent(child.firstParagraph, {
                        projectRelativeBaseFolderPath: getFolderJs(
                          child.projectRelativeMarkdownPath
                        )!,
                        projectRelativeMarkdownFilePath:
                          child.projectRelativeMarkdownPath,
                        augmentedWordObject,
                      })}
                    </Div>
                  ) : null}
                </Div>
              </ALink>
            );
          })}
        </Div>
      </Div>
    );
  };

  // console.log({ markdownFile });
  return (
    <Div>
      {markdownFile?.headerImage ||
      markdownFile?.headerTitle ||
      markdownFile?.header_markdownCallToActionSlugs ? (
        <ReaderPageHeader
          markdownFile={markdownFile}
          projectRelativeMarkdownPath={projectRelativeMarkdownPath}
        />
      ) : null}
      <Div className="pb-4 px-4">
        {props.children ? renderNav() : renderPage()}
      </Div>
    </Div>
  );
}

