clearTsDatabase.rawText:
 async (operationName: string | undefined) => {
  const result = await Promise.all(
    typescriptIndexModels.map((modelName) => {
      return db.clear(modelName, { operationName });
    })
  );

  await db.update(
    "Operation",
    () => true,
    (old) =>
      mergeNestedObject(old, {
        operation: {
          sizeIndexed: undefined,
          buildResultIndexed: undefined,
          classificationIndexed: undefined,
          folderNameIndexed: undefined,
          coreDependenciesIndexed: undefined,
          hasGeneratedDependenciesIndexed: undefined,
          operationDependenciesIndexed: undefined,
          packageDependenciesIndexed: undefined,
          relativeOperationLocationPathIndexed: undefined,
        },
      }),
    { operationName }
  );
}

executeCommandQuietUnlessFail.rawText:
 (config: {
  command: string;
  cwd?: string;
  /**
   * if given, will show what is happening and a checkmark if it succeeds
   */
  description?: string;
}): boolean => {
  const { command, cwd, description } = config;
  if (description) {
    process.stdout.write(`${description} `);
  }

  try {
    const result = execSync(command, {
      cwd,
      encoding: "utf8",
      stdio: "pipe",
    });

    if (description) console.log("‚úÖ");

    return true;
  } catch (e: any) {
    if (description) console.log("‚ùå");
    const error: {
      status: number;
      signal: any;
      output: (string | null)[];
      pid: number;
      stdout: string;
      stderr: string;
    } = e;
    console.log(`${command} went wrong:`, error?.stdout);

    return false;
  }
}

exitIfProcessDependenciesChanged.rawText:
 async (
  operationName: string,
  manualProjectRoot?: string
) => {
  const imports = (
    await Promise.all(
      ["watch-operations", "rebuild-operation"].map(
        async (operationName) =>
          // @ts-ignore
          db.get("TsImport", { operationName, manualProjectRoot }) as Promise<
            TsImport[]
          >
      )
    )
  )
    .flat()
    .map((i: TsImport) => i.module)
    // apparently this isn't a dependency of the above 3
    .concat(["watch-operations"])
    .filter(onlyUnique);

  if (operationName && imports.includes(operationName)) {
    log(`One of our dependencies (${operationName}) changed. Let's restart.`, {
      type: "important",
    });
    process.exit(1);
  }
}

generateJsonSchemas.rawText:
 async (
  manualProjectRoot?: string,
  /**
   * If given, does it just for a single operation
   */
  operationName?: string
) => {
  const projectRoot = manualProjectRoot || getProjectRoot();

  if (!projectRoot) return;

  const dbModels = await getAllDbModels(manualProjectRoot, operationName);

  const jsonDbModels = dbModels.filter(
    (x) =>
      x.dbStorageMethod &&
      ["jsonMultiple", "jsonSingle"].includes(x.dbStorageMethod)
  );
  /**
Loop:

- get the operationName for the model
- use the db to get all interfaces with that very operationName except for the one of the model...,
- get every `TsInterface.type.typeDefinition`
- put those inside of keys in the `defintions` key
- add the interface doccomment to the `title` key
- put the main properties in properties
- change copy `description` into `markdownDescription`, everywhere, to support Markdown Intellisense within JSON
- set `additionalProperties` to `false`
- set `"$schema": "http://json-schema.org/draft-07/schema#"`
- maybe also apply storage of db convention, so no `xxxCalculated` properties or `operationName` etc... (Same as `Storage<X>`)
- set `type: "object"`

         */

  const allSchemas = (
    await Promise.all(
      jsonDbModels.map(async (dbModel) => {
        if (!dbModel.dbStorageMethod) return;
        const locationPattern = getDefaultLocationPattern(
          dbModel.dbStorageMethod,
          dbModel.name
        );
        if (!locationPattern) return;

        const allInterfaces = await db.get("TsInterface", {
          // operationName: dbModel.operationName,
        });
        const mainSchema = dbModel.type.typeDefinition;

        const definitions = mergeObjectsArray(
          allInterfaces
            .filter((x) => x.name !== dbModel.name)
            .map((x) => {
              if (!x.type.typeDefinition) return;

              const goodName = x.name.replace(">", "%3E").replace("<", "%3C");
              return { [goodName]: x.type.typeDefinition };
            })
            .filter(notEmpty)
        );

        const schema: JSONSchema7 = {
          ...mainSchema,
          $schema: "http://json-schema.org/draft-07/schema#",
          title: dbModel.name,
          additionalProperties: false,
          description: dbModel.description,
          definitions,
        };

        return {
          schema,
          fileMatch: [`/${locationPattern}`],
          url: `./schemas/${pluralize(kebabCase(dbModel.name))}.schema.json`,
        };
      })
    )
  ).filter(notEmpty);

  /**
   The finished file should be put into `~/schemas/model-name.schema.json`

    Recalculate `json.schemas` in `project-root.code-workspace` (in project root)

    Put the storage convention in the `fileMatch` and the url to the project-relative schema in `url`...

   */
  const jsonSchemas = (
    await Promise.all(
      allSchemas.map(async (schemaObject) => {
        const isWriteSuccessful = await writeJsonToFile(
          path.join(projectRoot, schemaObject.url),
          schemaObject.schema
        );

        if (!isWriteSuccessful) {
          console.log("WRITE WERNT WRONG");
          return;
        }

        const { fileMatch, url } = schemaObject;
        const setting = { fileMatch, url };

        return setting;
      })
    )
  ).filter(notEmpty);

  const workspacePath = path.join(projectRoot, "project-root.code-workspace");
  const workspace =
    (await readJsonFile<{ [key: string]: any }>(workspacePath)) || {};

  const oldSchemas = workspace.settings?.["json.schemas"] || [];

  // make new workspace by concatenating the schemas with the old one and removing duplicates
  const newWorkspace = {
    ...workspace,
    settings: {
      ...workspace.settings,
      "json.schemas": jsonSchemas
        .concat(oldSchemas)
        .filter(
          onlyUnique2<{ fileMatch: string[]; url: string }>(
            (a, b) => a.url === b.url
          )
        ),
    },
  };

  const isNewWorkspaceWritten = await writeJsonToFile(
    workspacePath,
    newWorkspace
  );

  // console.log({ isNewWorkspaceWritten, workspacePath });
}

getAllDbModels.rawText:
 async (
  manualProjectRoot?: string,
  operationName?: string
) => {
  const allDbModels = (
    await db.get("TsInterface", { manualProjectRoot, operationName: "*" })
  )
    .filter(
      (x) => x.isDbModel && x.dbStorageMethod && x.isExported && !x.hasGeneric
    )
    .filter((x) => x.operationName !== "database")
    .filter((x) => (operationName ? x.operationName === operationName : true))
    .filter((x) => !!x.operationName) // && !dependants.includes(x.operationName)
    .filter(onlyUnique2<TsInterface>((a, b) => a.name === b.name));

  return allDbModels;
}

