renameToTemplateFile.rawText:
 (fileName: string) => {
  const extensionStartsAt = fileName.lastIndexOf(".");
  const insertPosition =
    extensionStartsAt === -1 ? fileName.length : extensionStartsAt;
  const beforeExtension = fileName.substring(0, insertPosition);
  const afterExtension = fileName.substring(insertPosition);

  return `${beforeExtension}${templateExtension}${afterExtension}`;
}

bodyFromQueryString.rawText:
 (
  /**
   * NB: everything AFTER The "?", so this should be the format: x=x&y=y&z=z&z=z2
   */
  query?: string
): QueryableObject | undefined => {
  if (!query) return;

  const keyValueObjectArray = query.split("&")?.map((x) => {
    const [key, value] = x.split("=");
    return { key, value };
  });

  const all = keyValueObjectArray?.reduce((object, keyValue) => {
    // NB: if that key already exists, let's make it an array and add this new value
    if (object[keyValue.key]) {
      object[keyValue.key] = [
        ...makeArray(object[keyValue.key]),
        keyValue.value,
      ];
      return object;
    }
    // Otherwise, just add the new key/value to the object.
    return { ...object, [keyValue.key]: keyValue.value };
  }, {} as QueryableObject);

  return all;
}

getFirstQueryStrings.rawText:
 (query: QueryableObject) => {
  const queryStrings = Object.keys(query).map((key) => {
    const value = query[key];
    return Array.isArray(value) ? value[0] : value;
  });

  return queryStrings;
}

toQueryString.rawText:
 (query?: any) => {
  const hasQuery =
    query && Object.entries(query)?.filter(isValidEntry).length > 0;
  return hasQuery
    ? "?" +
        Object.entries(query)
          .filter(isValidEntry)
          .map(([key, value]) => {
            const encodedValue = encodeURIComponent(String(value));
            return `${key}=${encodedValue}`;
          })
          .join("&")
    : "";
}

runChildProcess.rawText:
 async (config: {
  operationFolderName: string;
  /**
   * the location of the script in the build folder
   * NB: this must be a CLI that that actually executes the function!
   */
  scriptFileName: string;
  /**
   * all arguments you wish to pass to the script. If an argument is undefined, it will not be passed. Arguments to be passed must be strings
   */
  args?: (string | undefined)[];
}) => {
  const { operationFolderName, scriptFileName, args } = config;

  const childOperationPath = await getOperationPath(operationFolderName);
  if (!childOperationPath) {
    log(
      `Couldn't find childOperationPath for operation ${operationFolderName}`,
      { type: "error" }
    );
    return;
  }
  const scriptPath = path.join(
    childOperationPath,
    `build/${scriptFileName}.js`
  );

  if (!fs.existsSync(scriptPath)) {
    log(
      `Couldn't find scriptFile ${scriptFileName} which should be at build/${scriptFileName}.js`,
      { type: "error" }
    );
    return;
  }

  const readable = await canRead(scriptPath);

  if (!readable) {
    log(`Can't read ${scriptPath}`, { type: "error" });
    return;
  }

  const command = `node ${scriptPath} ${
    args?.filter(notEmpty).join(" ") || ""
  }`;

  // log(`going to execute: ${command}`, { type: "important" });

  const result = spawnSync(command, {
    shell: true,
    stdio: "inherit",
    encoding: "utf8",
  });

  return result.output;
}

