saySomething.rawText:
 async (
  input: string | SayOptions
): Promise<void> => {
  const paramKeys =
    typeof input === "string"
      ? null
      : (Object.keys(input) as (keyof SayOptions)[]);

  const params =
    typeof input === "string"
      ? null
      : paramKeys?.reduce((all, key) => {
          const value = input[key];

          if (value === undefined) {
            return all;
          }

          if (key === "message") {
            return `${all} "${value}"`;
          }

          return `${all} --${shorthands[key]}=${String(value)}`;
        }, "");

  const spawnInput = typeof input === "string" ? input : (params as string);

  const result = execSync(`say ${spawnInput}`, { stdio: "inherit" });

  return;
  // const res= spawn(`say ${spawnInput}`, {shell:true})
  // res.
}

textToMp3.rawText:
 async (
  options: Omit<SayOptions, "outputFilePath">,
  /**
   * Absolute path to the mp3
   */
  absoluteOutputFilePath: string
) => {
  const parsed = path.parse(absoluteOutputFilePath);
  const aiffPath = path.join(parsed.dir, `${parsed.name}.aiff`);
  const finalOptions: SayOptions = { ...options, outputFilePath: aiffPath };

  await saySomething(finalOptions);
  const finalPath = await convertToMp3(aiffPath, absoluteOutputFilePath);

  if (finalPath) {
    await fs.rm(aiffPath);
  }

  return finalPath;
}

findFirstCommentTypes.rawText:
 (
  strippedFullComment?: string
): CommentTypeObject => {
  if (strippedFullComment === undefined) return {};
  const lines = strippedFullComment.split("\n");

  const specialCommentTypesObject = commentTypes
    .map((commentType) => {
      const matchingPart = `${commentType.toUpperCase()}:`;
      const matchingLine = lines.find((line) => {
        const trimmedLine = line.trimStart();
        const isMatch = trimmedLine.startsWith(matchingPart);
        return isMatch;
      });

      if (!matchingLine) return;

      const strippedMatchingLine = matchingLine.trimStart();
      const specialCommentWithoutPrefix = strippedMatchingLine
        .slice(matchingPart.length)
        .trim();

      return {
        [commentType]: specialCommentWithoutPrefix,
      };
    })
    .filter(notEmpty);

  const fullObject = mergeObjectsArray(
    specialCommentTypesObject
  ) as CommentTypeObject;

  return fullObject;
}

getDataParameterNames.rawText:
 (
  item: AugmentedAnyModelType
): string[] => {
  const dataParameterNames = Object.keys(item)
    .map((key) => {
      const { dataParameterName } = getReferenceParameterInfo(key);

      return dataParameterName;
    })
    .filter(notEmpty);

  return dataParameterNames;
}

getPossibleReferenceParameterNames.rawText:
 (
  parameterName: string
): string[] => {
  const possibleReferenceParameterNames = isPlural(parameterName)
    ? referencePluralParameterNames
        .map(capitaliseFirstLetter)
        .map((ref) => `${singularize(parameterName)}${ref}`)
    : referenceParameterNames
        .map(capitaliseFirstLetter)
        .map((ref) => `${parameterName}${ref}`);

  return possibleReferenceParameterNames;
}

getProperties.rawText:
 (schema: Schema | undefined): SchemaProperty[] => {
  if (!schema) return [];
  const propertyKeys = schema.properties ? Object.keys(schema.properties) : [];
  const properties = propertyKeys
    .map((key) => {
      const propertySchema = getSchema(schema.properties?.[key]);
      return propertySchema
        ? {
            name: key,
            schema: propertySchema,
            required: schema.required?.includes(key) || false,
          }
        : null;
    })
    .filter(notEmpty);

  return properties;
}

getRefLink.rawText:
 (ref?: string) => {
  const refLink = ref
    ?.split("/")
    .pop()
    ?.replaceAll("%3C", "<")
    .replaceAll("%3E", ">");

  return refLink;
}

getReferencableModels.rawText:
 (
  simplifiedSchema?: SimplifiedSchema
): ReferenceParameterInfo[] | undefined => {
  if (simplifiedSchema?.type !== "object") return undefined;
  const parameterNames = simplifiedSchema.properties?.map((x) => x.name);

  const referenceParameterInfo = parameterNames
    ?.map((parameterName) => {
      const referenceParameterInfo = getReferenceParameterInfo(parameterName);

      return referenceParameterInfo;
    })
    .filter((x) => x.isReferenceParameter);

  return referenceParameterInfo;
}

getReferenceParameterInfo.rawText:
 (
  parameterName: string
): ReferenceParameterInfo => {
  const descriptorModelSplit = parameterName.split("_");
  const descriptor = parameterName.includes("_")
    ? descriptorModelSplit[0]
    : undefined;
  const rest = parameterName.includes("_")
    ? descriptorModelSplit[1]
    : parameterName;

  const wordArray = lowerCaseArray(rest);

  const singleWord = wordArray.length === 1;

  const parameterLastWord = wordArray.pop()!;

  const isReferenceSingleParameter =
    !singleWord && referenceParameterNames.includes(parameterLastWord);
  const isReferenceMultipleParameter =
    !singleWord && referencePluralParameterNames.includes(parameterLastWord);

  const isReferenceParameter =
    isReferenceSingleParameter || isReferenceMultipleParameter;

  // NB: the last item has been removed now
  const interfaceName = isReferenceParameter
    ? pascalCase(wordArray.join("-"))
    : undefined;

  // slug or id
  const keyInModel = isReferenceParameter
    ? singularize(parameterLastWord)
    : undefined;

  /**
   * the reference keyword should be removed from the parameterName to receive the dataParameterName
   *
   * e.g. `weirdSluggyModelSlugs` becomes `weirdSluggyModels`
   */
  const dataParameterName =
    isReferenceParameter && keyInModel
      ? replaceLastOccurence(
          parameterName,
          capitaliseFirstLetter(keyInModel),
          ""
        )
      : undefined;

  const referenceParameterInfo: ReferenceParameterInfo = {
    descriptor,
    keyInModel,
    interfaceName,
    isReferenceMultipleParameter,
    isReferenceSingleParameter,
    isReferenceParameter,
    dataParameterName,
    parameterName,
  };

  return referenceParameterInfo;
}

getSchemaItems.rawText:
 (schema: Schema | undefined) => {
  const schemas = makeArray(schema?.items).map(getSchema).filter(notEmpty);
  return schemas;
}

simplifiedSchemaToTypeDefinitionString.rawText:
 (
  simplifiedSchema?: SimplifiedSchema
) => {
  if (!simplifiedSchema) return "";

  if (simplifiedSchema.enum && simplifiedSchema.enum.length > 0) {
    // NB: TODO: This is probably not satisfactory for all enums! Needs testing
    const enumString = simplifiedSchema.enum.map((x) => String(x)).join(" | ");

    return enumString;
  }

  if (simplifiedSchema.type === "boolean") return "boolean";
  if (simplifiedSchema.type === "null") return "null";
  if (simplifiedSchema.type === "number") return "number";
  if (simplifiedSchema.type === "string") return "string";
  if (
    simplifiedSchema.type === "array" &&
    simplifiedSchema.items &&
    simplifiedSchema.items.length >= 1
  ) {
    const parts: string = simplifiedSchema.items
      .map((x) => simplifiedSchemaToTypeDefinitionString(x.schema))
      .join(" | ");

    const partsString: string =
      simplifiedSchema.items && simplifiedSchema.items.length >= 2
        ? `(${parts})[]`
        : `${parts}[]`;

    return partsString;
  }

  if (simplifiedSchema.type === "object" && simplifiedSchema.properties) {
    const objectParts = simplifiedSchema.properties.map((prop) => {
      const descriptionString = prop.schema.description
        ? `/** ${prop.schema.description} */\n`
        : "";
      const punctuationString = `${prop.required ? "" : "?"}: `;
      const propertyString: string = `${descriptionString}${
        prop.name
      }${punctuationString}${simplifiedSchemaToTypeDefinitionString(
        prop.schema
      )};`;

      return propertyString;
    });

    const objectString = `{\n${objectParts.join("\n")}\n};\n`;

    return objectString;
  }

  // NB: Should never be the case, we have handled all types
  return "";
}

simplifySchema.description:
Return a SimplifiedSchema by giving the JSONSchema7 schema, its name and a list of possible references in the JSONSchema.

A SimplifiedSchema is a data structure that allows you to easily define type interfaces that need to build out forms.

# Todo

Dual types aren't done right yet. I probably don't look at `anyOf` yet, which makes it result in an empty object.

For example, this one is problematic:

INPUT:

```json
{
"schema": {
"anyOf": [
{"type": "string"},
{"type": "array","items": {"type": "string"}}
]
},
```

Output:
```json
{
"simplifiedSchema": {
"properties": [],
"type": "object"
},
}
```

To test this one, test `npx rebuildOperation filename-conventions`

simplifySchema.rawText:
 (
  /** The name of the type interface, (this could be used as $ref). */
  name: string | null,

  /** The schema that needs to be simplified */
  schema: JSONSchema7,

  /** The array of other schemas found when crawling file this schema was found in. this also includes all refs to other type interfaces in all schemas in that file */
  possibleRefs: {
    /** the name of the schema, (without /definitions/ prefix like $ref) */
    name: string;
    schema: JSONSchema7;
  }[],
  /**
   * This function is recursive. If we find any reference to another schema, we will add the name of the current schema to the rootStack and explore that schema.
   */
  rootStack: string[]
): SimplifiedSchema | undefined => {
  if (Array.isArray(schema.type)) {
    // let's do this one later
    log(
      `I don't support this usecase (type is an array of multiple types)... ${schema.type.join(
        ","
      )}`,
      {
        type: "debug",
      }
    );
  }
  const type = Array.isArray(schema.type) ? schema.type[0] : schema.type;
  const newRootStack = name ? rootStack.concat(name) : rootStack;
  const refName = getRefLink(schema.$ref);

  // NB: we already encountered this ref before, let's avoid infinite recursion here.

  const isCircularRef = !!refName && rootStack.includes(refName);

  if (refName && !isCircularRef) {
    const refSchema = possibleRefs.find((r) => r.name === refName);
    if (!refSchema) {
      log(`ref not present: ${refName}`, { type: "warning" });
      // log("Strange, ref was not present in the possible refs", {
      //   type: "debug",
      // });

      // console.log({
      //   possibleRefNames: possibleRefs.map((x) => x.name),
      //   refName,
      // });
    }

    const thisDescription = schema.description
      ? `${schema.description}\n\n`
      : "";
    const mergedSchema = refSchema?.schema
      ? {
          ...refSchema.schema,
          description: `${thisDescription}${
            refSchema.schema.description || ""
          }`,
        }
      : undefined;

    return mergedSchema
      ? simplifySchema(refName, mergedSchema, possibleRefs, newRootStack)
      : undefined;
  }

  const fullComment = schema.description;

  const commentTypeObject = findFirstCommentTypes(fullComment);
  // TODO: Add all other `CommentType`s as properties
  const simplifiedPrimitive = {
    enum: schema.enum,
    circularRefName: refName,
    fullComment,
    ...commentTypeObject,
  };

  if (type === "boolean") {
    return { ...simplifiedPrimitive, type: "boolean" };
  }

  if (type === "integer" || type === "number") {
    // NB: integers are also numbers
    return { ...simplifiedPrimitive, type: "number" };
  }

  if (type === "null") {
    return { ...simplifiedPrimitive, type: "null" };
  }

  if (type === "string") {
    return { ...simplifiedPrimitive, type: "string" };
  }

  if (type === "array") {
    const items = getSchemaItems(schema);

    const simplifiedItems: SimplifiedSchemaItem[] = items
      .map((item) => {
        const itemName = getRefLink(item.$ref) || null;
        const schema = simplifySchema(
          itemName,
          item,
          possibleRefs,
          name ? rootStack.concat(name) : rootStack
        );
        if (!schema) return;
        return {
          schema,
          name: itemName,
        };
      })
      .filter(notEmpty);

    return {
      ...simplifiedPrimitive,
      items: simplifiedItems,
      type: "array",
    };
  }

  // NB: type must be an object here, it's the only possibility left...
  // in case of objects

  const properties = getProperties(schema);

  const simplifiedProperties: SimplifiedSchemaProperty[] = properties
    .map((property) => {
      const schema = simplifySchema(
        property.name,
        property.schema,
        possibleRefs,
        newRootStack
      );

      if (!schema) return;

      const possibleReferenceParameterNames =
        getPossibleReferenceParameterNames(property.name);

      const hasReferenceParameter = !!properties.find((x) =>
        possibleReferenceParameterNames.includes(x.name)
      );

      // NB: if the property has a model reference, we just need the model reference, not the whole model. This is only for retreiving, it's not present in the database.
      if (hasReferenceParameter) return;

      return {
        name: property.name,
        required: property.required,
        schema,
      };
    })
    .filter(notEmpty);

  return {
    ...simplifiedPrimitive,
    properties: simplifiedProperties,
    type: "object",
  };
}

addAuthenticationMethod.rawText:
 async (
  method: AuthenticationMethodMethod,
  handle: string,
  /**
   * TODO: check if it's unique before sending an email.
   * This is needed in case you are a person trying to add a method, because then there might be another person with the same handle.
   */
  shouldBeUnique?: boolean,
  credential?: string
): Promise<{
  isSuccessful: boolean;
  message: string;
  authenticationMethod?: AuthenticationMethod;
}> => {
  if (method === "email") {
    // send email to `handle`

    if (!isEmail(handle)) {
      return {
        isSuccessful: false,
        message: "Please provide a correct email",
      };
    }

    if (shouldBeUnique) {
      const isAuthenticationMethodUnavaiable =
        await findAuthenticatedPersonWithHandle(method, handle);
      if (isAuthenticationMethodUnavaiable) {
        console.log({ isAuthenticationMethodUnavaiable });
        return {
          isSuccessful: false,
          message: "That email is already taken",
        };
      }
    }

    const otp = Math.round(100000 + Math.random() * 900000);

    const text = `Please verify your email with this code: ${otp}. You have 10 minutes.`;

    const authenticationMethod: AuthenticationMethod = {
      method: "email",
      handle,
      isAuthenticated: false,
      otp,
    };

    const mailResult = await sendMail({
      to: handle,
      subject: "Verify your email",
      text,
    });

    if (!mailResult) {
      return { isSuccessful: false, message: "Couldn't send email" };
    }

    return {
      isSuccessful: true,
      message: "Code sent, please verify.",
      authenticationMethod,
    };
  }

  if (method === "phoneNumber") {
    // send sms to `handle`

    if (!isPhoneNumber(handle)) {
      return {
        isSuccessful: false,
        message: "Please provide a correct phone number",
      };
    }

    if (shouldBeUnique) {
      const isAuthenticationMethodUnavaiablePerson =
        await findAuthenticatedPersonWithHandle(method, handle);
      if (isAuthenticationMethodUnavaiablePerson) {
        console.log({ isAuthenticationMethodUnavaiablePerson });
        return {
          isSuccessful: false,
          message: "That phone number is already taken",
        };
      }
    }

    const otp = Math.round(100000 + Math.random() * 900000);
    const body = `Please verify your phone number with this code: ${otp}. You have 10 minutes.`;

    const authenticationMethod: AuthenticationMethod = {
      method: "phoneNumber",
      handle,
      isAuthenticated: false,
      otp,
    };

    const smsResult = await sendSms({ to: handle, body });

    if (!smsResult) {
      return { isSuccessful: false, message: "Couldn't send sms" };
    }

    return {
      isSuccessful: true,
      message: "Code sent, please verify.",
      authenticationMethod,
    };
  }

  if (method === "usernamePassword") {
    // add username and password to the authenticated methods

    if (!credential || !isValidPassword(credential)) {
      return {
        isSuccessful: false,
        message: "Please provide a valid password",
      };
    }

    const encryptedCredential = encryptPassword(credential);

    const authenticationMethod: AuthenticationMethod = {
      method: "usernamePassword",
      handle,
      encryptedCredential,
      credential,
      isAuthenticated: true,
    };

    return {
      isSuccessful: true,
      message: "Username/password has been set.",
      authenticationMethod,
    };
  }

  return { isSuccessful: false, message: "method not implemented yet" };
}

addDeviceAuthenticationMethodConfirm.rawText:
 async (
  /** device id */
  deviceId: string,
  method: AuthenticationMethodMethod,
  /** one time password */
  otp: number
) => {
  /////

  const device = (await db.get("Device")).find((x) => x.id === deviceId);

  if (!device) {
    return { isSuccessful: false, message: "Couldn't find device" };
  }

  const authenticatedMethod = device.authenticationMethods.find(
    (m) => m.method === method
  );

  if (authenticatedMethod?.isAuthenticated) {
    return {
      isSuccessful: false,
      message:
        "You already authenticated with this method, please delete it first before you add another one.",
    };
  }

  if (!authenticatedMethod) {
    return { isSuccessful: false, message: "Can't find method" };
  }

  if (["email", "phoneNumber"].includes(method)) {
    const isSuccessful = otp === authenticatedMethod.otp;

    if (isSuccessful) {
      // add isAuthenticated to device's authenticationMethods

      const newAuthenticatedMethod: AuthenticationMethod = {
        ...authenticatedMethod,
        otp: undefined,
        isAuthenticated: true,
      };

      const newauthenticationMethods = device.authenticationMethods.map((x) =>
        x.method === method && x.otp === otp ? newAuthenticatedMethod : x
      );

      const dbPath = getRootPath("db")!;
      const devicePath = path.join(dbPath, "devices", `${deviceId}.json`);

      writeJsonToFile(devicePath, {
        ...device,
        authenticationMethods: newauthenticationMethods,
      });
    }
    return {
      isSuccessful,
      message: isSuccessful ? "Successful" : "Incorrect code",
    };
  }
}

addDeviceAuthenticationMethodWithContext.rawText:
 async (
  functionContext: FunctionContext,
  method: AuthenticationMethodMethod,
  handle: string,
  credential?: string
): Promise<{
  isSuccessful?: boolean;
  message?: string;
  functionContext?: FunctionContext;
  authenticationMethod?: AuthenticationMethod;
}> => {
  const { isSuccessful, message, authenticationMethod } =
    await addAuthenticationMethod(method, handle, false, credential);
  if (!isSuccessful || !authenticationMethod) {
    return { isSuccessful, message };
  }
  // removes this method if you was alrady trying the same method (basically overwrite)
  const cleanAuthenticationMethods =
    functionContext.device.authenticationMethods?.filter(
      (x) => x.method !== authenticationMethod.method
    ) || [];

  // then adds the new one
  const newAuthenticationMethods =
    cleanAuthenticationMethods.concat(authenticationMethod);

  const updateResult = await db.update(
    "Device",
    (item) => item.authToken === functionContext.authToken,
    (old) => {
      return {
        ...old,
        authenticationMethods: newAuthenticationMethods,
      };
    }
  );

  const newFunctionContext = mergeNestedObject(functionContext, {
    device: {
      authenticationMethods: newAuthenticationMethods,
    },
  });

  return {
    isSuccessful: updateResult.isSuccesful,
    message: "Method added",
    functionContext: newFunctionContext,
    authenticationMethod,
  };
}

