loginWithContext.rawText:
 async (
  functionContext: FunctionContext
): Promise<{ isSuccessful: boolean; message: string }> => {
  const { device } = functionContext;

  const findPersonsResult = await findLoggedinPersonsWithContext(
    functionContext
  );

  if (!findPersonsResult.isSuccessful) {
    return {
      isSuccessful: findPersonsResult.isSuccessful,
      message: findPersonsResult.message,
    };
  }

  const personIds = findPersonsResult.persons?.map((x) => x.id);

  const updateResult = await db.update(
    "Device",
    (item) => item.id === device.id,
    (old) => ({
      ...old,
      personIds,
      authenticationMethods: [],
      currentPersonId: personIds?.[0],
    })
  );

  const isSuccessful = updateResult.isSuccesful || false;

  return {
    isSuccessful,
    message: isSuccessful
      ? "Logged in"
      : updateResult.message || "Something went wrong (weird)",
  };
}

loginWithPasswordWithContext.rawText:
 async (
  functionContext: FunctionContext,
  username: string,
  password: string
): Promise<{ isSuccessful: boolean; message?: string }> => {
  // first we have to add the authenticationMethod to the device
  const addAuthResult = await addDeviceAuthenticationMethodWithContext(
    functionContext,
    "usernamePassword",
    username,
    password
  );

  if (
    !addAuthResult.isSuccessful ||
    !addAuthResult.authenticationMethod ||
    !addAuthResult.functionContext
  ) {
    return {
      isSuccessful: addAuthResult.isSuccessful || false,
      message: addAuthResult.message,
    };
  }

  // now it should be possible to connect the auth method to the device
  const loginResult = await loginWithContext(addAuthResult.functionContext);
  const { isSuccessful, message } = loginResult;
  return {
    isSuccessful,
    message,
  };
}

logoutWithContext.rawText:
 async (
  functionContext: FunctionContext,
  rememberAuthentication?: boolean
) => {
  const { device } = functionContext;

  if (!device.personIds || !device.currentPersonId) {
    return { isSuccessful: false, message: "You were already logged out" };
  }

  const personAuthenticationMethods =
    device.currentPersonCalculated?.authenticationMethods || [];

  const newPersonIds = device.personIds.filter(
    (id) => id !== device.currentPersonId
  );

  if (newPersonIds.length === device.personIds.length) {
    return {
      isSuccessful: false,
      message: "Nobody logged out, this is strange...",
    };
  }

  // remove the person from the device, optionally moving the auth on the device again, so you can easily login again.

  const updateResult = await db.update(
    "Device",
    (item) => item.id === device.id,
    (old) => {
      const newAuthenticationMethods = rememberAuthentication
        ? old.authenticationMethods.concat(personAuthenticationMethods)
        : old.authenticationMethods;
      return {
        ...old,
        personIds: newPersonIds,
        currentPersonId: newPersonIds[newPersonIds.length - 1],
        authenticationMethods: newAuthenticationMethods,
      };
    }
  );

  const isSuccessful =
    !!updateResult.isSuccesful && updateResult.amountUpdated === 1;

  return {
    isSuccessful,
    message: isSuccessful
      ? "Logged out"
      : updateResult.message ||
        "Something went wrong (No updateResult message)",
  };
}

