  const authenticationMethod = person.authenticationMethods.find(
    (x) => x.method === method
  );

  if (!authenticationMethod) {
    return { isSuccessful: false, message: "You didn't add that method yet" };
  }

  const newAuthenticationMethods = person.authenticationMethods.filter(
    (x) => x.method !== method
  );

  const updateResult = await db.update(
    "Person",
    (item) => item.id === functionContext.device.currentPersonId,
    (old) => ({ ...old, authenticationMethods: newAuthenticationMethods })
  );

  return {
    isSuccessful: updateResult.isSuccesful,
    message: updateResult.isSuccesful ? "Removed" : updateResult.message,
  };
}

signupWithContext.rawText:
 async (
  functionContext: FunctionContext,
  /**
   * Data required for creating a `Person`. Can be filled in by the user partly, but also partly automatically
   */
  personData: SignupPersonData
): Promise<{ isSuccessful: boolean; message: string }> => {
  const { device } = functionContext;

  console.log(`Signup with context`);
  if (
    device.authenticationMethods.filter((x) => x.isAuthenticated).length === 0
  ) {
    return { isSuccessful: false, message: "Please authenticate first" };
  }

  const methodsAugmented = await Promise.all(
    device.authenticationMethods.map(async (method) => {
      const alreadyPerson = await findAuthenticatedPersonWithHandle(
        method.method,
        method.handle
      );

      return { ...method, alreadyPerson };
    })
  );

  const unavailableMethod = methodsAugmented.find(
    (method) => method.alreadyPerson
  );

  if (unavailableMethod) {
    const methodString = englishMethod[unavailableMethod.method].toLowerCase();
    return {
      isSuccessful: false,
      message: `There is already someone else with the ${methodString} ${unavailableMethod.handle}`,
    };
  }

  // it seesms fine , lets' create a new person

  const newPersonId = generateId();
  const newPerson: Creation<Person> = {
    id: newPersonId,
    authenticationMethods: device.authenticationMethods.map(
      // remove the credential
      ({ credential, ...other }) => other
    ),
    ...personData,
  };

  //@ts-ignore
  const upsertResult = await db.upsert("Person", newPerson, {
    onlyInsert: true,
  });

  if (!upsertResult.isSuccesful) {
    return {
      isSuccessful: false,
      message: upsertResult.message || "Upsert person not successful",
    };
  }

  const updateResult = await db.update(
    "Device",
    (item) => item.id === device.id,
    (old) => {
      console.log(
        `set new person id to ${newPersonId}, remove authenticationMethods`
      );
      return {
        ...old,
        authenticationMethods: [],
        personIds: old.personIds
          ? old.personIds.concat(newPersonId)
          : [newPersonId],
        currentPersonId: newPersonId,
      };
    }
  );

  return {
    isSuccessful: updateResult.isSuccesful || false,
    message: updateResult.isSuccesful
      ? "Signed up"
      : updateResult.isSuccesful || "Something went wrong, no mess",
  };
}

signupWithPasswordWithContext.rawText:
 async (
  functionContext: FunctionContext,
  fullName: string,
  username: string,
  pictureImage: BackendAsset | undefined,
  password: string,
  repeatPassword: string
): Promise<{ isSuccessful: boolean; message: string }> => {
  // processing is a oneliner for this type of functions!
  pictureImage = await processAsset(pictureImage);

  // only supported for now
  const method: AuthenticationMethodMethod = "usernamePassword";

  if (password !== repeatPassword) {
    return { isSuccessful: false, message: "Those passwords don't match" };
  }

  const addDeviceAuthResult = await addDeviceAuthenticationMethodWithContext(
    functionContext,
    method,
    username,
    password
  );

  // console.log({ addDeviceAuthResult });
  const {
    functionContext: newFunctionContext,
    authenticationMethod,
    isSuccessful,
    message,
  } = addDeviceAuthResult;

  if (!authenticationMethod?.isAuthenticated || !newFunctionContext) {
    console.log("NOT AUTHENTICATED AFTER ADDING DEVICE", {
      authenticationMethod,
      newFunctionContext,
    });
    return { isSuccessful: false, message: message || "Strange" };
  }

  const signupResult = await signupWithContext(newFunctionContext, {
    name: fullName,
    slug: slugify(fullName),
    pictureImage,
  });

  return signupResult;
}

