useAllText.rawText:
 () => {
  // Use a state to store the current value of the body element's text
  const [text, setText] = React.useState(
    typeof document === "undefined" ? undefined : document.body.innerText
  );

  // When the text of the body element changes, update the state
  React.useEffect(() => {
    if (typeof document === "undefined") return;

    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (mutation.type === "childList") {
          setText(document.body.innerText);
        }
      });
    });

    observer.observe(document.body, {
      childList: true,
      subtree: true,
    });

    // Return a cleanup function that stops observing changes
    return () => observer.disconnect();
  }, []);

  // Return the current text of the body element
  return text;
}

useLastSelection.rawText:
 (
  isDisabled?: boolean
): { selection: string | null; reset: () => void } => {
  // Initialize a ref to store the last selection
  const [selection, setSelection] = useState<string | null>(null);

  // Create a callback that updates the ref when the user makes a selection
  const updateLastSelection = () => {
    if (typeof window === "undefined") return null;

    const newSelection = window.getSelection()?.toString() || null;

    if (!newSelection) return null;

    setSelection(newSelection);

    return null;
  };

  // Add the event listener when the hook is called
  useEffect(() => {
    // Add the event listener to the document, so it will fire whenever the user makes a selection anywhere on the page
    document.addEventListener("selectionchange", updateLastSelection);

    // Return a cleanup function that removes the event listener
    return () => {
      document.removeEventListener("selectionchange", updateLastSelection);
    };
  }, []); // We only want to add the event listener once, so we pass an empty array as the second argument to useEffect

  if (isDisabled) {
    return { selection: "", reset: () => setSelection(null) };
  }
  // Return the last selection
  return { selection, reset: () => setSelection(null) };
}

