findAudioWithViewsArray.rawText:
 (
  content: string,
  index: number,
  title?: string
): AudioWithViews[] => {
  const relativeEmbeds = findEmbeds(content)
    .filter((x) => isPathRelative(x.src))
    .filter((x) => x.type !== "other" && x.type !== "text");

  const initialViewEmbeds: ViewEmbed[] = [
    title ? { title, audioKey: `audio${index}.0` } : undefined,
  ].filter(notEmpty);

  const initialValue: AudioWithViews[] = [
    {
      audioPath: null,
      viewEmbeds: initialViewEmbeds,
      audioKey: `audio${index}.0`,
    },
  ];

  const audioWithViewsArray = relativeEmbeds.reduce(
    (previous, current, currentIndex) => {
      // Create a new item in the AudioWithViews for a new audio
      if (current.type === "audio") {
        previous.push({
          audioPath: current.src,
          viewEmbeds: [],
          audioKey: `audio${index}.${currentIndex}`,
        });
        return previous;
      }

      // If a view is encountered, add it to the viewEmbeds array
      if (current.type === "image" || current.type === "video") {
        const quoteCharacters = "&quot;";
        const isSpoken =
          current.alt.startsWith(quoteCharacters) &&
          current.alt.endsWith(quoteCharacters);

        if (isSpoken) {
          // add new one because this has a spokentext.

          const spokenText = isSpoken
            ? current.alt.substring(
                quoteCharacters.length,
                current.alt.length - quoteCharacters.length
              )
            : undefined;

          const audioKey = `audio${index}.${currentIndex}`;
          previous.push({
            audioPath: null,
            viewEmbeds: [
              {
                viewPath: current.src,
                audioKey,
                spokenText,
              },
            ],
            audioKey,
          });
        } else {
          // attach to the lastone
          previous[previous.length - 1].viewEmbeds.push({
            viewPath: current.src,
            audioKey: previous[previous.length - 1].audioKey,
            spokenText: undefined,
          });
        }
      }

      // Do nothing for other types
      return previous;
    },
    initialValue
  );

  return audioWithViewsArray;
}

markdownParseToShortMarkdown.rawText:
 (
  markdownParse: MarkdownParse
): ShortMarkdown | undefined => {
  if (!markdownParse.content) return;

  // TODO: Make a nested function for this that actually works... -.-
  const paragraphs: { title?: string; paragraph: string }[] =
    markdownParse.content?.[0]?.children?.map((child) => {
      return {
        title: child.title,
        paragraph:
          child.content || child.children?.[0]?.content || "No content",
      };
    }) || [];

  const audioWithViewsArray = paragraphs
    .map((p, index) => {
      const viewSlides = findAudioWithViewsArray(p.paragraph, index, p.title);
      return viewSlides;
    })
    .flat()
    // No empty items
    .filter((x) => x.audioPath || x.viewEmbeds.length > 0);

  const shortMarkdown: ShortMarkdown = {
    // Can add this option later
    backgroundMusicTrack: [],
    audioWithViewsArray,
  };

  return shortMarkdown;
}

