augmentShortMarkdown.rawText:
 async (
  shortMarkdown: ShortMarkdown,
  projectRelativeFilePath: string
) => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;

  const absoluteFilePath = path.join(projectRoot, projectRelativeFilePath);

  const fileParse = path.parse(absoluteFilePath);

  const audioWithViewsArray = await Promise.all(
    shortMarkdown.audioWithViewsArray.map(async (audioWithViews) => {
      const spokenText = audioWithViews.audioPath
        ? undefined
        : audioWithViews.viewEmbeds.find((x) => x.spokenText)?.spokenText;
      const absoluteSpeechFilePath = path.join(
        fileParse.dir,
        `${fileParse.name}.speech-${audioWithViews.audioKey}.mp3`
      );

      const finalSpeechFilePath = spokenText
        ? await textToMp3({ message: spokenText }, absoluteSpeechFilePath)
        : undefined;
      const relativeSpeechFilePath = finalSpeechFilePath
        ? "./" + makeRelative(finalSpeechFilePath, fileParse.dir)
        : undefined;

      const audioPath =
        audioWithViews.audioPath || relativeSpeechFilePath || null;

      const audioDurationMs = finalSpeechFilePath
        ? await new Promise<number | undefined>((resolve) =>
            ffprobe(finalSpeechFilePath, (err, data) =>
              resolve((data.format.duration || 0) * 1000)
            )
          )
        : undefined;

      const newAudioWithViews: AudioWithViews = {
        audioPath,
        audioDurationMs,
        audioKey: audioWithViews.audioKey,
        viewEmbeds: audioWithViews.viewEmbeds.map((x) => {
          const durationMs = audioDurationMs
            ? audioDurationMs / audioWithViews.viewEmbeds.length
            : MINIMUM_DURATION_MS;
          return { ...x, durationMs };
        }),
      };

      return newAudioWithViews;
    })
  );

  const newShortMarkdown: ShortMarkdown = {
    ...shortMarkdown,
    audioWithViewsArray,
  };

  const indexFilePath = path.join(fileParse.dir, `${fileParse.name}.json`);
  await writeJsonToFile(indexFilePath, newShortMarkdown);

  return newShortMarkdown;
}

