parseDialogue.rawText:
 (
  textMarkdown: string
): { voice: string | undefined; sentence: string }[] => {
  const sentences = textMarkdown
    .split("\n")
    .map((line) => (line.trim() === "" ? undefined : line))
    .filter(notEmpty)
    .map((line) => {
      const parts = line.split(":");
      const voice = parts.length < 2 ? undefined : parts.shift();
      const sentence = parts.join(":").trim();

      return { voice, sentence };
    });

  return sentences;
}

