ShortMarkdownPlayer.rawText:
 (props: {
  shortMarkdown?: ShortMarkdown;
  projectRelativeFilePath?: string;
}) => {
  const { shortMarkdown, projectRelativeFilePath } = props;
  const [slide, setSlide] = useState<number | null>(null);

  const audioUrls =
    shortMarkdown?.audioWithViewsArray
      .filter((x) => !!x.audioPath)
      .map((x) => {
        if (!x.audioPath || !projectRelativeFilePath) return;

        const src = getSrc(
          { relativePath: x.audioPath },
          projectRelativeFilePath
        ).src;

        return { audioKey: x.audioKey, src };
      })
      .filter(notEmpty) || [];

  const audioPlayer = useMultiAudio(audioUrls.map((x) => x.src));

  const items = shortMarkdown?.audioWithViewsArray
    .map((x) => x.viewEmbeds)
    .flat();

  useEffect(() => {
    if (!items) return;
    if (slide === null) return;
    // 1 - find corresponding item
    const item = items[slide];

    // 2 - find the audio connected to it and where it should start for that audio
    const audioIndex = audioUrls.findIndex((x) => x.audioKey === item.audioKey);

    if (audioIndex === -1) return;

    // 3 - stop the previous audio, start next audio
    audioPlayer.toggle(audioIndex)();
  }, [slide]);

  if (!shortMarkdown) return null;
  if (!projectRelativeFilePath) return null;
  if (!items) return null;

  return (
    <Div className="h-[80vh] overflow-auto snap-mandatory snap-y">
      {items.map((item, index) => {
        return (
          <div id={`slide${index}`}>
            <ShortMarkdownSlide
              projectRelativeFilePath={projectRelativeFilePath}
              setSlide={setSlide}
              isAutoplay
              item={item}
              index={index}
              key={`slidekey${index}`}
            />
          </div>
        );
      })}
    </Div>
  );
}

ShortMarkdownSlide.rawText:
 (props: {
  item: ViewEmbed;
  index: number;
  projectRelativeFilePath: string;
  setSlide: (index: number) => void;
  isAutoplay?: boolean;
}) => {
  const { item, index, projectRelativeFilePath, setSlide, isAutoplay } = props;

  const [timeoutRef, setTimeoutRef] = useState<NodeJS.Timeout | null>(null);
  const slideRef = useRef<HTMLDivElement>(null);
  const isOnScreen = useOnScreen(slideRef);

  useEffect(() => {
    if (isOnScreen) {
      setSlide(index);

      const durationMs = item.durationMs;

      console.log({ index, durationMs });

      if (!durationMs) {
        return;
      }

      const timeout = setTimeout(() => {
        if (isAutoplay) {
          console.log("times up, should scroll to next!");
          setSlide(index + 1);

          const nextElement = document.getElementById(`slide${index + 1}`);

          if (!nextElement) {
            console.log("Couldn't find next");
            return;
          }

          console.log(
            "scrolling into view the next element",
            nextElement.id,
            "????",
            nextElement.getBoundingClientRect()
          );

          nextElement.scrollIntoView({ behavior: "smooth", block: "start" });
        }
      }, durationMs);

      setTimeoutRef(timeout);
    }

    if (!isOnScreen && timeoutRef) {
      clearTimeout(timeoutRef);
    }

    return () => {
      if (timeoutRef) {
        clearTimeout(timeoutRef);
      }
    };
  }, [isOnScreen]);

  const src =
    item.viewPath && projectRelativeFilePath
      ? getSrc({ relativePath: item.viewPath }, projectRelativeFilePath, !isDev)
          .src
      : undefined;

  return (
    <Div
      ref={slideRef}
      className="relative snap-center h-[80vh] flex justify-center items-center border-2 border-black"
    >
      {src ? (
        item.viewPath?.endsWith(".png") ? (
          <Div className="absolute top-0 left-0 w-full h-full bg-blue-200">
            <Image src={src} width="100%" objectFit="fill" layout="fill" />
          </Div>
        ) : item.viewPath?.endsWith(".mov") ? (
          <Div className="absolute top-0 left-0 w-full h-full bg-blue-200">
            <video
              muted
              src={src}
              autoPlay
              loop
              preload="none"
              width="100%"
              style={{ objectFit: "fill" }}
            />
          </Div>
        ) : null
      ) : null}
      {item.title ? <P className="text-8xl font-bold">{item.title}</P> : null}
    </Div>
  );
}

