    onChangeParameter(newArray);
  };

  const removeItem = (index: number) => {
    if (!parameterValue) return;
    const newArray = removeIndexFromArray(parameterValue, index);
    onChangeParameter(newArray);
  };

  const createArray = () => {
    if (parameterValue && parameterValue.length > 0) return;
    onChangeParameter([undefined]);
  };
  const shouldShowClear = parameter.required
    ? parameterValue && parameterValue.length > 0
    : parameterValue !== undefined;
  const clearArray = () => {
    // NB: clearing will set it to undefined, unless the array is required, then we should just set it to an empty array.
    onChangeParameter(parameter.required ? [] : undefined);
  };

  const requiredText = parameter.required ? "(Required)" : "(Optional)";
  const arrayLengthText = parameterValue
    ? parameterValue.length > 0
      ? `Array (${parameterValue.length})`
      : `Empty array`
    : `undefined`;
  return (
    <Div
      className={"ml-2 pl-2 my-4 border-l border-gray-900 dark:border-gray-100"}
    >
      <Div className="flex flex-row justify-between">
        <P>
          <Span className="font-bold">{humanCase(parameter.name || "")} </Span>
          {requiredText} {arrayLengthText}
        </P>
        <Div className="flex flex-row">
          {shouldShowClear ? (
            <ClickableIcon onClick={clearArray} emoji="‚ùå" />
          ) : null}
          {parameterValue === undefined || parameterValue.length === 0 ? (
            <ClickableIcon onClick={createArray} emoji="‚ûï" />
          ) : null}
        </Div>
      </Div>

      {isDebug ? (
        <P className="italic text-gray-600">
          {parameter.simplifiedSchema.description}
        </P>
      ) : null}

      {/* NB: we need a form for every item in the array */}
      {parameterValue?.map((arrayItem, arrayIndex) => {
        const simplifiedSchema: SimplifiedSchema | undefined =
          parameter.simplifiedSchema?.items?.[0]?.schema;

        const hasName = parameter.name ? parameter.name.length > 0 : false;
        return (
          <Div>
            <SimplifiedSchemaForm
              modelName={modelName}
              itemNameOrId={itemNameOrId}
              projectRelativeStorageFilePath={projectRelativeStorageFilePath}
              isDebug={isDebug}
              parameterNameStack={
                hasName
                  ? parameterNameStack
                    ? [...parameterNameStack, parameter.name]
                    : [parameter.name]
                  : parameterNameStack
              }
              parameters={[
                {
                  name: `${parameter.name} item ${arrayIndex + 1}`,
                  required: true,
                  simplifiedSchema,
                  isDbModel: false,
                  renderButtons: () => {
                    return (
                      <Div className="flex flex-row">
                        {arrayIndex === 0 ? (
                          <ClickableIcon
                            onClick={() => addItemAbove(arrayIndex)}
                            emoji="‚Üë"
                          />
                        ) : null}
                        <ClickableIcon
                          onClick={() => addItemBelow(arrayIndex)}
                          emoji="‚Üì"
                        />
                        <ClickableIcon
                          onClick={() => removeItem(arrayIndex)}
                          emoji="Àü"
                        />
                      </Div>
                    );
                  },
                },
              ]}
              onChange={(newArrayValues) => {
                const newArrayValue = newArrayValues[0];

                // set this index of the array to the new value taken from the form
                parameterValue[arrayIndex] = newArrayValue;
                // only change the value that contains the array
                onChangeParameter(parameterValue);
              }}
              values={[arrayItem]}
              // just passing this
              referencableModelData={referencableModelData}
              id={`${id}.${arrayIndex}`}
            />
          </Div>
        );
      })}
    </Div>
  );
}

