getReferencedModelDataItem.rawText:
 (
  parameterName: string,
  referencedModelData?: ReferencableModelData
): ReferencedModelDataItem | undefined => {
  const interfaceInfo = getReferenceParameterInfo(parameterName);
  const referencedData: ReferenceItemsObject | undefined =
    interfaceInfo &&
    interfaceInfo.interfaceName &&
    referencedModelData &&
    referencedModelData[interfaceInfo.interfaceName]
      ? referencedModelData[interfaceInfo.interfaceName]
      : undefined;

  return interfaceInfo
    ? {
        ...referencedData,
        ...interfaceInfo,
      }
    : undefined;
}

ObjectForm.rawText:
 (props: {
  itemNameOrId?: string;
  parameterNameStack?: string[];
  projectRelativeStorageFilePath?: string;
  parameter: RenderableFunctionParameter;
  /**
   * In this case this must be an object or undefined
   */
  parameterValue: { [key: string]: any } | undefined;
  onChangeParameter: (newValue: any) => void;

  isDebug?: boolean;
  referencableModelData?: ReferencableModelData;
  referencedModelDataIsLoading?: boolean;

  /**
   * Unique id for this ObjectForm
   */
  id: string;
  modelName?: string;
}) => {
  const {
    modelName,
    itemNameOrId,
    parameterNameStack,
    projectRelativeStorageFilePath,
    parameter,
    onChangeParameter,
    parameterValue,
    isDebug,
    id,
    referencableModelData,
    referencedModelDataIsLoading,
  } = props;

  /**
   * By default, the thing is expanded if the parameter is required, or if there is already a parameter value. If it is "true" by default, you can also not minimise
   */
  const defaultExpandedState = parameter.required ? true : !!parameterValue;

  const [isExpanded, setIsExpanded] = useState(defaultExpandedState);

  if (!parameter.simplifiedSchema) {
    return null;
  }

  // we can only render the object if it has properties...
  if (
    !parameter.simplifiedSchema.properties ||
    parameter.simplifiedSchema.properties.length === 0
  ) {
    // console.log("Object without properties", { parameter });
    return isDebug ? (
      <P className="text-red-500">
        Object without properties {parameter.name}{" "}
        {JSON.stringify(parameter.simplifiedSchema)}
      </P>
    ) : null;
  }

  // for each parameter, recurse this form builder

  const onChangeSubset = (objectParameterValues: any[]) => {
    // NB: every item in the array is linked with a parameter in the object
    const objectParts = parameter.simplifiedSchema?.properties?.map((x, i) => ({
      [x.name]: objectParameterValues[i],
    }));
    const objectOrNot = objectParts
      ? mergeObjectsArray(objectParts)
      : undefined;

    onChangeParameter(objectOrNot);
  };

  const valuesSubset: any[] = parameter.simplifiedSchema.properties.map(
    (x) => parameterValue?.[x.name]
  );

  const parameters: RenderableFunctionParameter[] =
    parameter.simplifiedSchema.properties.map((x) => ({
      name: x.name,
      required: x.required,
      simplifiedSchema: x.schema,
      isDbModel: parameter.isDbModel,
    }));

  // console.log({ parameters, parameter, isExpanded });

  const renderExpandButton = () => {
    if (defaultExpandedState) return null;

    return (
      <ClickableIcon
        onClick={() => setIsExpanded(!isExpanded)}
        emoji={isExpanded ? "-" : "+"}
      />
    );
  };

  const hasName = parameter.name ? parameter.name.length > 0 : false;

  return (
    <Div
      className={"ml-2 pl-2 my-4 border-l border-gray-900 dark:border-gray-100"}
    >
      {renderParameterTitle(parameter, isDebug, true, () =>
        renderExpandButton()
      )}

      {isExpanded ? (
        <SimplifiedSchemaForm
          modelName={modelName}
          itemNameOrId={itemNameOrId}
          parameterNameStack={
            hasName
              ? parameterNameStack
                ? [...parameterNameStack, parameter.name]
                : [parameter.name]
              : parameterNameStack
          }
          projectRelativeStorageFilePath={projectRelativeStorageFilePath}
          isDebug={isDebug}
          parameters={parameters}
          onChange={onChangeSubset}
          values={valuesSubset}
          // just passing this
          referencableModelData={referencableModelData}
          id={id}
        />
      ) : null}
    </Div>
  );
}

ReferenceInput.rawText:
 (props: {
  parameter: FunctionParameter;
  parameterValue: any;
  onChangeParameter: (newValue: any) => void;
  referencedModelDataItem: ReferencedModelDataItem;
  defaultInputFields: {
    uniqueFieldId: string;
    fieldName: string;
  };
  isDebug?: boolean;
}) => {
  const {
    isDebug,
    parameter,
    parameterValue,
    onChangeParameter,
    defaultInputFields,
    referencedModelDataItem,
  } = props;

  const isLoading = referencedModelDataItem.isLoading;

  const addModelItem = () => {
    return (
      <ClickableIcon
        emoji="‚ûï"
        onClick={() => {
          if (typeof window === undefined) return;

          window.open(
            `upsert/${referencedModelDataItem.interfaceName}`,
            "_blank"
          );
        }}
      />
    );
  };

  // selectbox, if referencedModelData is available
  if (referencedModelDataItem.isReferenceMultipleParameter) {
    // select multiple
    const value = (parameterValue || []) as string[];

    const onChangeMultiple = (selected: Item<string>[]) => {
      const newValue =
        selected.length === 0 && !parameter.required
          ? undefined
          : selected.map((x) => x.value);
      onChangeParameter(newValue);
    };

    const options: Item<string>[] =
      referencedModelDataItem.data
        ?.map((x) => {
          const label = x.name || x.id;
          const value = referencedModelDataItem.keyInModel
            ? (x[
                referencedModelDataItem.keyInModel as keyof ReferenceItem
              ] as string)
            : undefined;
          if (!value) return;

          return {
            value,
            label,
          };
        })
        .filter(notEmpty) || [];

    const selectedValues = value
      .map((v) => options.find((x) => x.value === v))
      .filter(notEmpty);

    return (
      <Div>
        {renderParameterTitle(parameter, isDebug, false, addModelItem)}
        {isLoading ? (
          <FancyLoader />
        ) : (
          <SelectMultipleInput
            {...defaultInputFields}
            onChange={onChangeMultiple}
            value={selectedValues}
            extra={{
              options,
              autoSuggest: options.length > 10,
            }}
            config={{}}
          />
        )}
      </Div>
    );
  } else {
    // select single

    // If a referenced model contains the `categoryStack` parameter, this data should be used to group the selectbox. Without categoryStack parameter, simply use the name.

    const value = parameterValue as string | undefined;

    const onChangeSingle = (selected: Item<string> | null) => {
      const newValue =
        !selected && !parameter.required ? undefined : selected?.value;
      onChangeParameter(newValue);
    };

    const options: Item<string>[] =
      referencedModelDataItem.data
        ?.map((x) => {
          const categoryStack = x.categoryStack;

          const value = referencedModelDataItem.keyInModel
            ? (x[
                referencedModelDataItem.keyInModel as keyof ReferenceItem
              ] as string)
            : undefined;

          if (!value) return;
          /**
           * This must be something. Name is not always there, but ID should be!
           */
          const identifier: string = x.name || x.id;
          const label = categoryStack
            ? categoryStack.concat(identifier).join(" ‚Ä∫ ")
            : identifier;

          return {
            value,
            label,
          };
        })
        .filter(notEmpty) || [];

    const selectValue = value
      ? {
          label:
            referencedModelDataItem.data?.find((x) =>
              referencedModelDataItem.keyInModel
                ? x[
                    referencedModelDataItem.keyInModel as keyof ReferenceItem
                  ] === value
                : false
            )?.name || "Select an option",
          value,
        }
      : null;

    const optionsAndFirst: Item<string>[] = [
      { label: "Select an option", value: "" },
    ].concat(options);

    return (
      <Div>
        {renderParameterTitle(parameter, isDebug, false, addModelItem)}
        {isLoading ? (
          <FancyLoader />
        ) : (
          <SelectInput
            {...defaultInputFields}
            onChange={onChangeSingle}
            value={selectValue}
            extra={{
              options: optionsAndFirst,
              autoSuggest: optionsAndFirst.length > 10,
            }}
            config={{}}
          />
        )}
      </Div>
    );
  }
}

