useReferencableModelData.rawText:
 (
  simplifiedSchema: SimplifiedSchema
): ReferencableModelData | undefined => {
  const referencableModelNames = getReferencableModels(simplifiedSchema)
    ?.map((x) => x.interfaceName)
    .filter(notEmpty);

  if (!referencableModelNames) return;

  /**
   * NB: here we just get the referencable model data for the referencable model names in this form. But since they are decoupled, useQuery is caching them, across forms!
   */
  const referencableModelDataArray = referencableModelNames
    .map((dbModelName) => {
      const realDbModelName = dbModelName as keyof DbModels;
      const { data: apiResult, isLoading } =
        queries.useGetReferencableModelData(realDbModelName);

      const data = apiResult?.result;

      return { [dbModelName]: { data, isLoading } };
    })
    .filter(notEmpty);

  const referencableModelData: ReferencableModelData | undefined =
    referencableModelDataArray
      ? mergeObjectsArray(referencableModelDataArray)
      : undefined;

  return referencableModelData;
}

