Tooltip.rawText:
 (props: {
  tooltip: React.ReactElement | null;
  children: React.ReactNode;
  hoverTimeout?: number;
  placement?: Placement;
}) => {
  const timeoutMs = props.hoverTimeout || DEFAULT_HOVER_TIMEOUT_MS;
  const [hoverLink, setHoverLink] = useState(false);

  const [tooltipTimeout, setTooltipTimeout] = useState<NodeJS.Timeout | null>(
    null
  );
  const [linkTimeout, setLinkTimeout] = useState<NodeJS.Timeout | null>(null);

  const btnRef = createRef<HTMLAnchorElement>();
  const popoverRef = createRef<HTMLSpanElement>();
  const [hoverTooltip, setHoverTooltip] = useState(false);

  const onEnterLink = () => {
    if (!btnRef.current || !popoverRef.current) return;
    createPopper(btnRef.current, popoverRef.current, {
      placement: props.placement || "auto",
    });
    setHoverLink(true);
    if (linkTimeout) {
      clearTimeout(linkTimeout);
    }
  };

  const onLeaveLink = () => {
    const timeout = setTimeout(() => setHoverLink(false), timeoutMs);
    setLinkTimeout(timeout);
  };

  const popoverShow = hoverTooltip || hoverLink;

  const onEnterTooltip = () => {
    if (tooltipTimeout) {
      clearTimeout(tooltipTimeout);
    }
    setHoverTooltip(true);
  };

  const onLeaveTooltip = () => {
    const timeout = setTimeout(() => setHoverTooltip(false), timeoutMs);
    setTooltipTimeout(timeout);
  };

  return (
    <span>
      {props.tooltip ? (
        <span
          onMouseEnter={onEnterTooltip}
          onMouseLeave={onLeaveTooltip}
