vscodeOpen.rawText:
 async (config: {
  /**
  Files to open. Either use a projectRelativePath or an operationName combined with an operationRelativePath.

  can optionally have the line and character specified as well after the file with [file-path]:[line]:[character]
   */
  files?: OpenableFile[];
}): Promise<undefined | { success: boolean; response: string }> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;

  if (!config.files) {
    const command = `code --goto ${projectRoot}`;
    execSync(command);
    return { success: true, response: "Opened the project root" };
  }

  await Promise.all(
    config.files.map(async (file) => {
      const absolutePath = await getOpenableFilePath(file);

      if (!absolutePath || !fs.existsSync(absolutePath)) {
        return { success: false, response: "This path doesn't exist" };
      }

      const fullPathWithLine = file.line
        ? `${absolutePath}:${file.line}`
        : absolutePath;

      const command = `code --goto ${fullPathWithLine}`;

      execSync(command);

      return { success: true, response: "Opened the file" };
    })
  );

  return { success: true, response: "Opened the file(s)" };
}

watchAll.rawText:
 async (config?: {
  /**
   * Overwrite the default ignored behavior.
   *
   * NB: is not overwriting the startup ignore behavior!
   */
  customIgnored?: string[];
}) => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) return;

  // NB: fix to globally alter real fs in order to fix EMFile error that happens in TSMorph (see https://github.com/isaacs/node-graceful-fs)
  gracefulFs.gracefulify(realFs);

  console.log("Searching..."); //
  const tsFunctions = await db.get("TsFunction");

  const projectWatcherTsFunctions = tsFunctions.filter(
    (x) => x.explicitTypeName === "ProjectWatcher"
  );

  const projectWatchers = projectWatcherTsFunctions
    .map((x) => x.name)
    .map((name) => sdk[name as keyof typeof sdk] as ProjectWatcher | undefined)
    .filter(notEmpty);

  log(`${projectWatchers.length} watchers found to watch ${projectRoot}`, {
    type: "important",
  });

  const startupWaitMs = 1000;
  setTimeout(() => {
    log(
      `There they are! \n\n${projectWatchers
        .map((projectWatcher) => {
          return `üëÅ üëÅ ${projectWatcher.name} ‚úÖ`;
        })
        .join("\n")}`,
      {
        type: "success",
      }
    );
  }, startupWaitMs);

  const startTimeAt = Date.now();

  const recordingsPath = process.env.HOME
    ? path.resolve(
        process.env.HOME,
        "Library/Application Support/com.apple.voicememos/Recordings"
      )
    : undefined;

  const ignored = config?.customIgnored || [
    "**/node_modules/**",
    "**/.next/**",
    "**/.expo/**",
    // "**/build/**", // build change detection is needed for SDK generation
    "**/db/**",
    "**/.git/**",
    "**/.turbo/**",
    "**/generated/**",
  ];

  const projectRootFolders = [
    "asset",
    "docs",
    "knowledge",
    "operations",
    "text",
  ].map((name) => path.join(projectRoot, name));

  const basePath = [...projectRootFolders, recordingsPath].filter(notEmpty);

  // do everything at startup too, as long as a "startupStrategy" is declared
  const startupFiles = (
    await explore({
      basePath,
      ignore: [
        "node_modules",
        ".next",
        ".expo",
        "db",
        "public",
        ".git",
        ".turbo",
        "generated",
      ],
    })
  ).map((x) => x.path);

  console.log({ startupFiles: startupFiles.length });
  const queueItemsBefore = await db.get("Queue");

  // go over every project watcher, add to queue
  const queueItems = await oneByOne(projectWatchers, async (watcher) => {
    if (watcher.startupStrategy === "ignore" || !watcher.startupStrategy) {
      console.log(`ignore ${watcher.name}`);
      return;
    }

    const thisQueueItemFilePaths = queueItemsBefore
      .filter((x) => x.functionName === watcher.name)
      .map((x) => x.parameters[1] as string);

    const realStartupFiles = startupFiles
      .filter((absolutePath) => {
        return watcher.filter("add", absolutePath);
      })
      .filter((x) => !thisQueueItemFilePaths.includes(x));

    console.log({
      watcher: watcher.name,
      filesAlreadyInQueue: thisQueueItemFilePaths.length,
      filesToBeAddedToQueue: realStartupFiles.length,
    });

    if (realStartupFiles.length === 0) {
      return;
    }

    const newQueueItems = realStartupFiles.map((absolutePath) => {
      const queue: Creation<Queue> = {
        functionName: watcher.name,
        parameters: ["add", absolutePath, true],
      };
      return queue;
    });

    return newQueueItems;
  });

  const newQueueItemsFinal = queueItems.filter(notEmpty).flat();

  const upsertResult = await db.upsert("Queue", newQueueItemsFinal);
  console.log({
    queueUpsertResultInsertedAmount: upsertResult,
    newQueueItemsFinal: newQueueItemsFinal.length,
  });

  watch(basePath, {
    ignoreInitial: true,
    ignored,
  }).on("all", async (eventName, path) => {
    const isStartup = Date.now() < startTimeAt + startupWaitMs;
    if (isStartup) {
      return;
    }

    const isOffline = !(await isOnline());
    const isBusy = await isSystemBusy();

    const relevantWatchers = projectWatchers.filter((watcher) => {
      const isApplicableWatcher = watcher.filter(eventName, path);
      if (!isApplicableWatcher) {
        return false;
      }

      if (watcher.config?.isInternetRequired && isOffline) {
        // add to queue because busy
        db.upsert("Queue", {
          functionName: watcher.name,
          parameters: [eventName, path, false],
        });
        return false;
      }

      if (watcher.config?.isHeavy && isBusy) {
        // add to queue because busy

        db.upsert("Queue", {
          functionName: watcher.name,
          parameters: [eventName, path, false],
        });
        return false;
      }

      return true;
    });

    oneByOne(relevantWatchers, async (projectWatcher) => {
      console.log(`watchAll: executing ${projectWatcher.name} for ${path}`);
      await projectWatcher(eventName, path, false);
    });
  });
}

