writeToAssets.rawText:
 async (
  /**
   * Insert `__filename` here or the typescript file path
   */
  filePath: string,
  /**
   * The data you want to write to a file, can also be a promise
   */
  data: any,
  assetsFileName?: string,
  hideLog?: boolean
) => {
  // NB: it may be a promise, so first await it
  const realData = await data;

  const operationBasePath = findOperationBasePath(filePath);
  if (!operationBasePath) return;

  const extension = typeof realData === "string" ? ".md" : ".json";

  const realAssetsFileName =
    assetsFileName || `${path.parse(filePath).name}${extension}`;
  const assetsFilePath = path.join(
    operationBasePath,
    "assets",
    realAssetsFileName
  );

  if (!hideLog) {
    console.log({ assetsFilePath });
  }

  if (typeof data === "string") {
    return writeStringToFile(assetsFilePath, realData);
  }

  return writeJsonToFile(assetsFilePath, realData);
}

copyPath.rawText:
 async (
  projectRelativePath: string
): Promise<{ isSuccessful: boolean; message?: string }> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return { isSuccessful: false, message: "No project root found" };
  }

  const absolutePath = path.join(projectRoot, projectRelativePath);

  if (!fs.existsSync(absolutePath)) {
    console.log({ absolutePath });
    return {
      isSuccessful: false,
      message: "This file or folder doesn't exist",
    };
  }

  const parsedPath = path.parse(absolutePath);
  const copiedPath = path.join(
    parsedPath.dir,
    `${parsedPath.name}-copy${parsedPath.ext}`
  );

  if (fs.existsSync(copiedPath)) {
    return {
      isSuccessful: false,
      message:
        "There is already a copy for this file/folder under the anticipated name.",
    };
  }

  await fs.cpAsync(absolutePath, copiedPath, { recursive: true });

  return { isSuccessful: true };
}

