updateFrontmatter.rawText:
 async (config: {
  projectRelativePath: string;
  frontmatter: { [key: string]: string };
}): Promise<{ isSuccessful: boolean; message?: string }> => {
  const projectRoot = getProjectRoot();
  if (!projectRoot) {
    return {
      isSuccessful: false,
      message: "No projectroot",
    };
  }

  const { frontmatter, projectRelativePath } = config;

  const absolutePath = path.join(projectRoot, projectRelativePath);

  const exists = fs.existsSync(absolutePath);
  if (!exists) {
    return { isSuccessful: false, message: "File/folder doensn't exist" };
  }

  const pathStats = await fs.stat(absolutePath);

  const isFolder = pathStats.isDirectory();

  const finalAbsolutePath = isFolder
    ? path.join(absolutePath, "README.md")
    : absolutePath;

  // merge frontmatter with old frontmatter

  const existsFinal = fs.existsSync(finalAbsolutePath);

  const markdownParse = existsFinal
    ? await readMarkdownFile(finalAbsolutePath)
    : null;
  const oldFrontmatter: Frontmatter = existsFinal
    ? markdownParse?.parameters || {}
    : {};

  const newFrontmatter: Frontmatter = { ...oldFrontmatter, ...frontmatter };

  const newMarkdownParse: MarkdownParse = markdownParse
    ? {
        ...markdownParse,
        parameters: newFrontmatter,
      }
    : {
        parameters: newFrontmatter,
        raw: "",
      };

  const string = markdownParseToMarkdownString(newMarkdownParse);

  console.log({ newString: string });
  await fs.writeFile(finalAbsolutePath, string, "utf8");
  return { isSuccessful: true };
}

