ContextTextArea.rawText:
 (props: EditorProps) => {
  const { value, onChange, withContext, className, projectRelativeFilePath } =
    props;

  const fileType = getFileTypeFromPath(props.projectRelativeFilePath);

  const contextualPromptsQuery = queries.useGetContextualPrompts(
    fileType === "other" ? undefined : fileType
  );

  const alert = useAlert();

  // EDITOR HOTKEYS
  useHotkeys([], ({ code, metaKey, ctrlKey, altKey, shiftKey, key }) => {
    if (code === "Escape") {
      // go out of intellisense
      alert?.("ESCAPE");
    }

    if (ctrlKey && code === "Space") {
      // intellisense on
      alert?.("intellisense on");
    }
    if (altKey && code === "KeyZ") {
      if (shiftKey) {
        alert?.("redo");
      } else {
        alert?.("undo");
      }
    }

    if (metaKey && key === "b") {
      alert?.("bold");
    }

    if (metaKey && key === "i") {
      alert?.("italic");
    }
  });

  return (
    <>
      <div className={className}>
        <textarea
          className={className}
          value={value}
          onChange={(e: React.ChangeEvent<HTMLTextAreaElement>) => {
            const newText = e.target.value;
            const positionIndex = e.target.selectionStart;
            const textEditingContext = getContext({
              text: newText,
              positionIndex,
            });
            withContext?.(textEditingContext);

            onChange(newText);
          }}
        />
      </div>
    </>
  );
}

