getTextSegments.rawText:
 (element: ChildNode | null): TextSegment[] => {
  if (element === null) return [];

  const children = Array.from(element.childNodes);

  const textSegments: TextSegment[] = children
    .map((node) => {
      if (node.nodeType === Node.TEXT_NODE) {
        return {
          text: node.nodeValue || "",
          node,
        };
      }

      if (node.nodeType === Node.ELEMENT_NODE) {
        // NB: not sure which nodes contain ID, but this must work

        if (node.nodeName === "DIV") {
          // NB: because nodeName is DIV, it must be a HTMLDivElement
          const divElement = node as HTMLDivElement;
          const isAugmentation = divElement.dataset.isAugmentation === "true";
          if (isAugmentation) return;
        }

        if (node.nodeName === "BR") {
          return { text: "\n", node };
        } else {
          return getTextSegments(node);
        }
      }

      return;
    })
    .filter(notEmpty)
    .flat();

  return textSegments;
}

