isAugmentedWordMatch.rawText:
 (
  augmentedWord: AugmentedWord,
  completableWord: string
) => {
  if (augmentedWord.isCaseInsensitive) {
    const isMatch = augmentedWord.word
      ? augmentedWord.word.toLowerCase().includes(completableWord.toLowerCase())
      : false;
    return isMatch;
  }

  const isMatch = augmentedWord.word
    ? augmentedWord.word.includes(completableWord)
    : false;
  return isMatch;
}

MarkdownCompletions.rawText:
 (props: {
  context: TextEditingContext | null;
  augmentedWords?: AugmentedWord[];
  augmentedWordObject?: MappedObject<AugmentedWord>;
}) => {
  const { augmentedWords, context, augmentedWordObject } = props;

  /*
    For `markdown` + `other`:

    - `@` = people select tooltip
    - backtick = AugmentedWord from Ts Statements
    - `![` = AssetInput tooltip
    - `_`, `*` or `**` is `AugmentedWord` from Word db
    - `#` should get a list of `Interest`s
  */

  // const isAsset = context?.wordAtPosition.startsWith("![");
  const isTypescriptWord = context?.wordAtPosition.startsWith("`");
  const isPersonWord = context?.wordAtPosition.startsWith("@");
  const isSpecialWord = specialCharacters.find((startSignal) =>
    context?.wordAtPosition.startsWith(startSignal)
  );

  const filteredAugmentedWords = augmentedWords
    ? isTypescriptWord
      ? augmentedWords.filter((x) =>
          ["operation", "bundle", "type", "function", "variable"].includes(
            x.type
          )
        )
      : isPersonWord
      ? augmentedWords.filter((x) => ["person"].includes(x.type))
      : isSpecialWord
      ? augmentedWords.filter((x) => ["word"].includes(x.type))
      : []
    : [];

  const completableWord =
    isTypescriptWord || isPersonWord
      ? context?.wordAtPosition.slice(1)
      : isSpecialWord
      ? omitSpecialCharactersFromStart(context?.wordAtPosition)
      : undefined;

  const completions = completableWord
    ? filteredAugmentedWords
        .filter((augmentedWord) => {
          const isMatch = isAugmentedWordMatch(augmentedWord, completableWord);

          return isMatch;
        })
        .slice(0, MAX_COMPLETIONS_AMOUNT)
    : [];

  return (
    <Div>
      {completions.map((completion, index) => {
        return (
          <Completion
            augmentedWordObject={augmentedWordObject}
            augmentedWord={completion}
            key={`completion${index}`}
          />
        );
      })}
    </Div>
  );
}

MarkdownParsePresentation.rawText:
 (props: {
  markdownParse: MarkdownParse;
  augmentedWordObject?: MappedObject<AugmentedWord>;
  projectRelativeBaseFolderPath: string;
  projectRelativeMarkdownFilePath: string;
}) => {
  const {
    markdownParse,
    augmentedWordObject,
    projectRelativeBaseFolderPath,
    projectRelativeMarkdownFilePath,
  } = props;
  const [slide, setSlide] = useState(0);

  useHotkey(
    (event) => event.keyCode === 37,
    () => setSlide((slide) => slide - 1),
    []
  );
  useHotkey(
    (event) => event.keyCode === 39,
    () => setSlide((slide) => slide + 1),
    []
  );

  if (!markdownParse.content) return null;

  const markdownParagraphs = flattenMarkdownChunks(markdownParse.content);

  const filtered = markdownParagraphs.filter((x) => x.paragraph !== "");

  console.log({ markdownParagraphs, filtered });

  const paragraph = filtered[slide];

  if (!paragraph) {
    return <P>No slides</P>;
  }

  const navigationIcons: ClickableIconType[] = [
    {
      onClick: () => setSlide(0),
      emoji: "‚èÆ",
      disabled: slide === 0,
    },

    {
      onClick: () => setSlide(slide - 1),
      emoji: "‚¨Ö",
      disabled: slide === 0,
    },

    {
      onClick: () => setSlide(slide + 1),
      emoji: "‚û°",
      disabled: slide === markdownParagraphs.length - 1,
    },

    {
      onClick: () => setSlide(markdownParagraphs.length - 1),
      emoji: "‚è≠",
      disabled: slide === markdownParagraphs.length - 1,
    },
  ];
  const navigation = (
    <Div className="flex flex-row gap-3">
      {navigationIcons.map((item, index) => (
        <ClickableIcon {...item} key={`icon${index}`} />
      ))}
    </Div>
  );

  const title = paragraph.categoryStack[paragraph.categoryStack.length - 1];

  return (
    <Div className="flex flex-col flex-1">
      <Div className="flex flex-1 flex-col h-screen">
        <Div className="flex flex-col justify-center items-center">
          <P className="text-4xl">{title}</P>

          <Div className="flex flex-row flex-wrap">
            {renderBreadCrumbs(paragraph.categoryStack)}
          </Div>
        </Div>

        <Div className="flex flex-1 flex-col items-center justify-center overflow-y-auto">
          <MarkdownContent
            content={paragraph.paragraph}
            config={{
              big: true,
              projectRelativeBaseFolderPath,
              projectRelativeMarkdownFilePath,
              augmentedWordObject,
              isDev,
              isStatic: false,
            }}
          />
        </Div>
      </Div>

      {navigation}
    </Div>
  );
}

