MarkedToken.rawText:
 (props: {
  item: marked.Token;
  subtextConfig: SubtextConfig;
  subwordConfig: SubwordConfig;
  markdownFileConfig: MarkdownParseRenderConfig;
  /**
   * If enabled, all content will be tested in realtime
   */
  testModeEnabled?: boolean;
}): JSX.Element => {
  const {
    item,
    markdownFileConfig,
    subtextConfig,
    subwordConfig,
    testModeEnabled,
  } = props;

  if (item.type === "heading") {
    const fontSize = depthFontSizes[item.depth as 1 | 2 | 3 | 4 | 5 | 6 | 7];

    const headingContent =
      item.tokens && item.tokens.length > 1 ? (
        `${`#`.repeat(item.depth)} ${item.tokens.map((token) => (
          <MarkedToken
            item={token}
            subtextConfig={subtextConfig}
            subwordConfig={subwordConfig}
            markdownFileConfig={markdownFileConfig}
            testModeEnabled={testModeEnabled}
          />
        ))}`
      ) : (
        <SpannedSentence
          sentence={item.raw}
          subwordConfig={subwordConfig}
          markdownFileConfig={markdownFileConfig}
          subtextConfig={subtextConfig}
        />
      );

    return (
      <>
        <p>
          <strong style={{ color: "blue", fontSize }}>{headingContent}</strong>
        </p>
        {getSubtext(item.text, subtextConfig)}
        <br />
      </>
    );
  }

  if (item.type === "space") {
    // 1 newline splits into two parts here, 5 newlines are 6 parts, so if we omit the first one, we get an array with the length of the amount of newlines
    const newlines = item.raw.split("\n").slice(1);

    return (
      <>
        {newlines.map(() => (
          <br />
        ))}
      </>
    );
  }

  if (item.type === "blockquote") {
    // > ....
    return (
      <p>
        <strong style={{ color: "blue" }}>&gt;</strong>
        <SpannedSentence
          sentence={item.text}
          subwordConfig={subwordConfig}
          markdownFileConfig={markdownFileConfig}
          subtextConfig={subtextConfig}
        />
      </p>
    );
  }

  if (item.type === "paragraph") {
    // Paragraph always has tokens?
    if (testModeEnabled) {
      testContentEditableRenderComponent(MarkedParagraph, item);
    }

    return (
      <MarkedParagraph
        markedToken={item}
        subwordConfig={subwordConfig}
        markdownFileConfig={markdownFileConfig}
        subtextConfig={subtextConfig}
      />
    );
  }

  if (item.type === "text") {
    if (testModeEnabled) {
      testContentEditableRenderComponent(MarkedText, item);
    }
    return (
      <MarkedText
        markedToken={item}
        subwordConfig={subwordConfig}
        markdownFileConfig={markdownFileConfig}
        subtextConfig={subtextConfig}
      />
    );
  }

  if (item.type === "list") {
    return (
      <>
        {item.items.map((token) => (
          <MarkedToken
            item={token}
            subtextConfig={subtextConfig}
            subwordConfig={subwordConfig}
            markdownFileConfig={markdownFileConfig}
            testModeEnabled={testModeEnabled}
          />
        ))}
      </>
    );
  }

  if (item.type === "list_item") {
    const beforeText = item.raw.split(item.text)[0];

    const text = (
      <MarkedText
        markedToken={item}
        subwordConfig={subwordConfig}
        subtextConfig={subtextConfig}
        markdownFileConfig={markdownFileConfig}
      />
    );

    return (
      <>
        <b style={{ color: "blue" }}>{beforeText}</b> {text}
        <br />
      </>
    );
  }

  if (item.type === "escape") {
    return (
      <b style={{ color: "red" }}>
        <MarkedText
          markedToken={item}
          subwordConfig={subwordConfig}
          markdownFileConfig={markdownFileConfig}
          subtextConfig={subtextConfig}
        />
      </b>
    );
  }

  if (item.type === "strong") {
    return (
      <b style={{ color: "blue" }}>
        <MarkedText
          markedToken={item}
          subwordConfig={subwordConfig}
          markdownFileConfig={markdownFileConfig}
          subtextConfig={subtextConfig}
        />
      </b>
    );
  }

  if (item.type === "em") {
    return (
      <i style={{ color: "blue" }}>
        <MarkedText
          markedToken={item}
          subwordConfig={subwordConfig}
          markdownFileConfig={markdownFileConfig}
          subtextConfig={subtextConfig}
        />
      </i>
    );
  }

  if (item.type === "image") {
    const realSrc = getRealSrc(item.href, markdownFileConfig);
    return (
      <div
        data-href={realSrc}
        className={metaClickableClassName}
        data-html="true"
        data-tip="<img src='${realSrc}' width=200 /><br>'Cmd+Click' to open"
      >
        {item.raw}
      </div>
    );
  }

  if (item.type === "link") {
    return (
      <div
        data-href={item.href}
        className={metaClickableClassName}
        data-html="true"
        data-tip="'Cmd+Click' to open"
      >
        {item.raw}
      </div>
    );
  }

  if (item.type === "html") {
    return (
      <span style={{ color: "green" }}>
        {item.raw
          .replaceAll("<", "&lt;")
          .replaceAll(">", "&gt;")
          .replaceAll("\n", "<br />")}
      </span>
    );
  }

  if (item.type === "codespan") {
    // Need to add tooltip here for augmented words

    const trimmedWord = trimAround(item.raw, 1);

    return (
      <span style={{ color: "orange" }}>
        \`
        {trimmedWord.split(" ").map((word) => {
          const augmentedWord = markdownFileConfig.augmentedWordObject?.[word];

          return (
            <span
              data-tip={
                augmentedWord && augmentedWord.spoiler
                  ? augmentedWord.spoiler
                  : undefined
              }
            >
              {word}
            </span>
          );
        })}
        \`
      </span>
    );
  }

  if (item.type === "code") {
    return (
      <span style={{ color: "orange" }}>
        <MarkedText
          markedToken={item}
          subwordConfig={subwordConfig}
          markdownFileConfig={markdownFileConfig}
          subtextConfig={subtextConfig}
        />
      </span>
    );
  }

  if (item.type === "table") {
    return (
      <span style={{ color: "pink" }}>
        <MarkedText
          markedToken={item}
          markdownFileConfig={markdownFileConfig}
          subtextConfig={subtextConfig}
          subwordConfig={subwordConfig}
        />
      </span>
    );
  }

  console.log(`unhandled: ${item.type}`, item);
  return <></>;
}

omitSpecialCharactersFromStart.rawText:
 (word?: string) => {
  if (word === undefined) return;

  const finalWord = specialCharacters.reduce(
    (word, character) => {
      // NB: if a word was already trimmed from a special character, the other special characters don't need to be trimmed
      if (word.hasChanged) return word;
      const newWord = trimLeft(word.word, character);
      return { word: newWord, hasChanged: word.word !== newWord };
    },
    { word, hasChanged: false }
  );

  return finalWord.word;
}

parseTextContentToHtmlString.rawText:
 (
  text,
  type,
  subtextConfig,
  subwordConfig,
  markdownParseRenderConfig
) => {
  if (type === "markdown" && markdownParseRenderConfig) {
    const lexed = marked.lexer(text);

    const html = lexed.map((token) => (
      <MarkedToken
        item={token}
        subtextConfig={subtextConfig}
        subwordConfig={subwordConfig}
        markdownFileConfig={markdownParseRenderConfig}
        testModeEnabled={true}
      />
    ));

    const result = <>{html}</>;

    const string = ReactDOMServer.renderToString(result);

    return string;
  }

  return text;
}

