byteCount.rawText:
/**
 * This function will return the byte size of any UTF-8 string you pass to it.
 */
export function byteCount(s: string) {
  return encodeURI(s).split(/%..|./).length - 1;
}

isObject.rawText:
export function isObject(object: any): object is object {
  return object != null && typeof object === "object";
}

Modal.rawText:
export function Modal() {
  const [inside, setInside] = useState(false);
  let { modalContent, title, handleModal, showModal } =
    useContext(ModalContext);

  if (showModal) {
    return (
      <>
        <Div
          onClick={() => !inside && handleModal(null)}
          className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto outline-none focus:outline-none"
        >
          <Div
            onMouseEnter={() => setInside(true)}
            onMouseLeave={() => setInside(false)}
            className="relative w-auto max-w-3xl mx-auto my-6 lg:w-10/12 lg:h-10/12"
          >
            {/*content*/}
            <Div className="relative flex flex-col w-full bg-white border-0 rounded-lg shadow-lg outline-none focus:outline-none">
              {/*header*/}
              <Div className="flex items-start justify-between p-5 border-b border-gray-200 border-solid rounded-t">
                <h3 className="text-3xl font-semibold">{title}</h3>
                <Button
                  className="float-right p-1 ml-auto text-3xl font-semibold leading-none text-black bg-transparent border-0 outline-none opacity-5 focus:outline-none"
                  onClick={() => handleModal(null)}
                >
                  <span className="block w-6 h-6 text-2xl text-black bg-transparent outline-none opacity-5 focus:outline-none">
                    x
                  </span>
                </Button>
              </Div>
              {/*body*/}
              <Div className="relative flex-auto p-6">
                <p className="my-4 text-lg leading-relaxed text-gray-500">
                  {modalContent}
                </p>
              </Div>
              {/*footer*/}
              <Div className="flex items-center justify-end p-6 border-t border-gray-200 border-solid">
                <Button
                  className="px-6 py-2 mb-1 mr-1 text-sm font-bold text-red-500 uppercase transition-all duration-150 ease-linear outline-none background-transparent focus:outline-none"
                  onClick={() => handleModal(null)}
                >
                  Close...
                </Button>
              </Div>
            </Div>
          </Div>
        </Div>
        <Div className="fixed inset-0 z-40 bg-black opacity-50"></Div>
      </>
    );
  } else return null;
}

useNavigation.rawText:
export function useNavigation() {
  const router = useRouter();

  return {
    addListener: () => {},
    canGoBack: () => true,
    dispatch: () => {},
    getParent: () => {},
    getState: () => {},
    goBack: () => {
      router.back();
    },
    isFocused: () => {},
    removeListener: () => {},
    reset: () => {},
    setOptions: () => {},
    setParams: () => {},

    navigate: (screen: string, params?: { [key: string]: any }) => {
      router.push(screen, { query: params });
    },
  };
}

useIsInViewport.rawText:
export function useIsInViewport(ref: React.MutableRefObject<Element | null>) {
  const [isIntersecting, setIsIntersecting] = useState(false);

  const observer = useMemo(
    () =>
      typeof window !== "undefined"
        ? new IntersectionObserver(([entry]) =>
            setIsIntersecting(entry.isIntersecting)
          )
        : undefined,
    []
  );

  useEffect(() => {
    if (!ref.current) return;

    observer?.observe(ref.current);

    return () => {
      observer?.disconnect();
    };
  }, [ref, observer]);

  return isIntersecting;
}

setKeyAtLocation.rawText:
export function setKeyAtLocation(path: string, value: any, object: any) {
  var schema = object; // a moving reference to internal objects within obj
  var pList = path.split(".");
  var len = pList.length;
  for (var i = 0; i < len - 1; i++) {
    var elem = pList[i];
    if (!schema[elem]) schema[elem] = {};
    schema = schema[elem];
  }

  schema[pList[len - 1]] = value;
}

getChunkBegin.rawText:
// ====================================
// The functions below are created to handle multibyte utf8 characters.
// To understand how the encoding works, check this article: https://en.wikipedia.org/wiki/UTF-8#Encoding
// @todo add documentation for these

function getChunkBegin(buf: Buffer, chunkBegin: number) {
  // If it's the beginning, just return.
  if (chunkBegin === 0) {
    return 0;
  }

  if (!isLaterByteOfUtf8(buf[chunkBegin])) {
    return chunkBegin;
  }

  let begin = chunkBegin - 3;

  if (begin >= 0) {
    if (isFirstByteOf4ByteChar(buf[begin])) {
      return begin;
    }
  }

  begin = chunkBegin - 2;

  if (begin >= 0) {
    if (
      isFirstByteOf4ByteChar(buf[begin]) ||
      isFirstByteOf3ByteChar(buf[begin])
    ) {
      return begin;
    }
  }

  begin = chunkBegin - 1;

  if (begin >= 0) {
    // Is it a 4-byte, 3-byte utf8 character?
    if (
      isFirstByteOf4ByteChar(buf[begin]) ||
      isFirstByteOf3ByteChar(buf[begin]) ||
      isFirstByteOf2ByteChar(buf[begin])
    ) {
      return begin;
    }
  }

  return -1;
}

getChunkEnd.rawText:
function getChunkEnd(buf: Buffer, chunkEnd: number) {
  // If it's the end, just return.
  if (chunkEnd === buf.length) {
    return chunkEnd;
  }

  let index = chunkEnd - 3;

  if (index >= 0) {
    if (isFirstByteOf4ByteChar(buf[index])) {
      return chunkEnd + 1;
    }
  }

  index = chunkEnd - 2;

  if (index >= 0) {
    if (isFirstByteOf4ByteChar(buf[index])) {
      return chunkEnd + 2;
    }

    if (isFirstByteOf3ByteChar(buf[index])) {
      return chunkEnd + 1;
    }
  }

  index = chunkEnd - 1;

  if (index >= 0) {
    if (isFirstByteOf4ByteChar(buf[index])) {
      return chunkEnd + 3;
    }

    if (isFirstByteOf3ByteChar(buf[index])) {
      return chunkEnd + 2;
    }

    if (isFirstByteOf2ByteChar(buf[index])) {
      return chunkEnd + 1;
    }
  }

  return chunkEnd;
}

getEncoding.rawText:
/**
 * Get the encoding of a buffer.
 * Checks the start, middle, and end of the buffer for characters that are unrecognized within UTF8 encoding.
 * History has shown that inspection at all three locations is necessary.
 * @returns Will be `null` if `buffer` was not provided. Otherwise will be either `'utf8'` or `'binary'`
 */
export function getEncoding(
  buffer: Buffer | null,
  opts?: EncodingOpts
): "utf8" | "binary" | null {
  // Check
  if (!buffer) return null;

  // Prepare
  const textEncoding = "utf8";
  const binaryEncoding = "binary";
  const chunkLength = opts?.chunkLength ?? 24;
  let chunkBegin = opts?.chunkBegin ?? 0;

  // Discover
  if (opts?.chunkBegin == null) {
    // Start
    let encoding = getEncoding(buffer, { chunkLength, chunkBegin });
    if (encoding === textEncoding) {
      // Middle
      chunkBegin = Math.max(0, Math.floor(buffer.length / 2) - chunkLength);
      encoding = getEncoding(buffer, {
        chunkLength,
        chunkBegin,
      });
      if (encoding === textEncoding) {
        // End
        chunkBegin = Math.max(0, buffer.length - chunkLength);
        encoding = getEncoding(buffer, {
          chunkLength,
          chunkBegin,
        });
      }
    }

    // Return
    return encoding;
  } else {
    // Extract
    chunkBegin = getChunkBegin(buffer, chunkBegin);
    if (chunkBegin === -1) {
      return binaryEncoding;
    }

    const chunkEnd = getChunkEnd(
      buffer,
      Math.min(buffer.length, chunkBegin + chunkLength)
    );

    if (chunkEnd > buffer.length) {
      return binaryEncoding;
    }

    const contentChunkUTF8 = buffer.toString(
      textEncoding,
      chunkBegin,
      chunkEnd
    );

    // Detect encoding
    for (let i = 0; i < contentChunkUTF8.length; ++i) {
      const charCode = contentChunkUTF8.charCodeAt(i);
      if (charCode === 65533 || charCode <= 8) {
        // 8 and below are control characters (e.g. backspace, null, eof, etc.)
        // 65533 is the unknown character
        // console.log(charCode, contentChunkUTF8[i])
        return binaryEncoding;
      }
    }

    // Return
    return textEncoding;
  }
}

isBinary.rawText:
/**
 * Determine if the filename and/or buffer is binary.
 * Determined by extension checks first (if filename is available), otherwise if unknown extension or no filename, will perform a slower buffer encoding detection.
 * This order is done, as extension checks are quicker, and also because encoding checks cannot guarantee accuracy for chars between utf8 and utf16.
 * The extension checks are performed using the resources https://github.com/bevry/textextensions and https://github.com/bevry/binaryextensions
 * @param filename The filename for the file/buffer if available
 * @param buffer The buffer for the file if available
 * @returns Will be `null` if neither `filename` nor `buffer` were provided. Otherwise will be a boolean value with the detection result.
 */
export function isBinary(filename?: string | null, buffer?: Buffer | null) {
  const text = isText(filename, buffer);
  if (text == null) return null;
  return !text;
}

isFirstByteOf2ByteChar.rawText:
function isFirstByteOf2ByteChar(byte: number) {
  // eslint-disable-next-line no-bitwise
  return byte >> 5 === 6; // 110xxxxx?
}

isFirstByteOf3ByteChar.rawText:
function isFirstByteOf3ByteChar(byte: number) {
  // eslint-disable-next-line no-bitwise
  return byte >> 4 === 14; // 1110xxxx?
}

isFirstByteOf4ByteChar.rawText:
function isFirstByteOf4ByteChar(byte: number) {
  // eslint-disable-next-line no-bitwise
  return byte >> 3 === 30; // 11110xxx?
}

isLaterByteOfUtf8.rawText:
function isLaterByteOfUtf8(byte: number) {
  // eslint-disable-next-line no-bitwise
  return byte >> 6 === 2; // 10xxxxxx?
}

isText.rawText:
/**
 * Determine if the filename and/or buffer is text.
 * Determined by extension checks first (if filename is available), otherwise if unknown extension or no filename, will perform a slower buffer encoding detection.
 * This order is done, as extension checks are quicker, and also because encoding checks cannot guarantee accuracy for chars between utf8 and utf16.
 * The extension checks are performed using the resources https://github.com/bevry/textextensions and https://github.com/bevry/binaryextensions
 * @param filename The filename for the file/buffer if available
 * @param buffer The buffer for the file if available
 * @returns Will be `null` if neither `filename` nor `buffer` were provided. Otherwise will be a boolean value with the detection result.
 */
export function isText(
  filename?: string | null,
  buffer?: Buffer | null
): boolean | null {
  // Test extensions
  if (filename) {
    // Extract filename
    const parts = pathUtil.basename(filename).split(".").reverse();

    // Cycle extensions
    for (const extension of parts) {
      if (textExtensions.indexOf(extension) !== -1) {
        return true;
      }
      if (binaryExtensions.indexOf(extension) !== -1) {
        return false;
      }
    }
  }

  // Fallback to encoding if extension check was not enough
  if (buffer) {
    return getEncoding(buffer) === "utf8";
  }

  // No buffer was provided
  return null;
}

useOnScreen.description:
/**
 * checks wheter or not a certain element is in the browsers screen view, or not.
 */

useOnScreen.rawText:
/**
 * checks wheter or not a certain element is in the browsers screen view, or not.
 */
export function useOnScreen(ref: RefObject<HTMLDivElement>) {
  const [isIntersecting, setIntersecting] = useState(false);

  const observer =
    typeof window === "undefined"
      ? null
      : new IntersectionObserver(([entry]) =>
          setIntersecting(entry.isIntersecting)
        );

  useEffect(() => {
    if (observer) {
      if (ref.current) {
        observer?.observe(ref.current);
      }
      // Remove the observer as soon as the component is unmounted
      return () => {
        observer?.disconnect();
      };
    }
  }, [observer]);

  return isIntersecting;
}

makeSubscription.rawText:
// `watch` is obtained from `resp.watch` in the `watch-project` response.
// `relative_path` is obtained from `resp.relative_path` in the
// `watch-project` response.
export function makeSubscription(
  client: watchman.Client,
  watchBaseFolder: string,
  watchRelativePath: string,
  debug: boolean
) {
  /**
   * this can probably be optimised
   */
  const sub = {
    // Match any `.js` file in the dir_of_interest
    expression: ["allof", ["match", "*.*"]],
    // Which fields we're interested in
    fields: ["name", "size", "mtime_ms", "exists", "type"],
    relative_root: undefined as undefined | string,
    // add our time constraint
  };

  if (watchRelativePath) {
    sub.relative_root = watchRelativePath;
  }

  const subName = getSubName(watchBaseFolder, watchRelativePath);

  client.command(
    ["subscribe", watchBaseFolder, subName, sub],
    function (error, resp) {
      if (error) {
        // Probably an error in the subscription criteria
        console.error("Failed to subscribe: ", error);
        return;
      }
      if (debug) {
        console.log(
          "New subscription:",
          "subscription " + resp.subscribe + " established"
        );
      }
    }
  );

  return subName;
}
